import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Flower = ({ delay, from, imageType, size = 60 }) => {
  const getInitialPosition = () => {
    switch (from) {
      case 'top':
        return { x: `${Math.random() * 100}%`, y: -150 };
      case 'bottom':
        return { x: `${Math.random() * 100}%`, y: 'calc(100vh + 150px)' };
      case 'left':
        return { x: -150, y: `${Math.random() * 100}%` };
      case 'right':
        return { x: 'calc(100vw + 150px)', y: `${Math.random() * 100}%` };
      case 'top-left':
        return { x: -150, y: -150 };
      case 'top-right':
        return { x: 'calc(100vw + 150px)', y: -150 };
      case 'bottom-left':
        return { x: -150, y: 'calc(100vh + 150px)' };
      case 'bottom-right':
        return { x: 'calc(100vw + 150px)', y: 'calc(100vh + 150px)' };
      default:
        return { x: `${Math.random() * 100}%`, y: -150 };
    }
  };

  // Random final positions across the entire screen
  const getFinalPosition = () => {
    return {
      x: `${Math.random() * 90 + 5}%`, // 5% to 95% to avoid edges
      y: `${Math.random() * 90 + 5}%`, // 5% to 95% to avoid edges
    };
  };

  const getImageSrc = () => {
    switch (imageType) {
      case 'cherry-blossom':
        return '/flowers-images/cherry-blossom.png';
      case 'flower':
        return '/flowers-images/flower.png';
      case 'sakura':
        return '/flowers-images/sakura.png';
      default:
        return '/flowers-images/flower.png';
    }
  };

  return (
    <motion.div
      className="absolute"
      style={{
        width: size,
        height: size,
        zIndex: 5, // Lower z-index to be in background
      }}
      initial={getInitialPosition()}
      animate={getFinalPosition()}
      transition={{
        delay: delay,
        duration: 2.5,
        ease: "easeOut",
      }}
    >
      <motion.img
        src={getImageSrc()}
        alt="Flower"
        className="w-full h-full object-contain"
        animate={{
          rotate: [0, 360],
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "linear",
        }}
      />
    </motion.div>
  );
};

const Page2 = () => {
  const [displayedText, setDisplayedText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showFlowers, setShowFlowers] = useState(false);
  const [showNextButton, setShowNextButton] = useState(false);
  const [showCursor, setShowCursor] = useState(true);

  const englishText = "I'm not very good at saying how much I love you. I know I don't always explain my feelings the right way. But I'm always trying to show it with my actions. I'm willing to do anything for you, and I really do my best. I love you even more because you understand this about me. You understand how I am, and you never make me feel bad about it. That's what makes you so special to me.\n\nThat's why I wanted to do something just for you.\nThese flowers are for you ðŸŒ¸ðŸ˜";

  useEffect(() => {
    // Start typing animation immediately when page loads
    const typingTimer = setTimeout(() => {
      setIsTyping(true);
      startTyping();
    }, 100);

    return () => clearTimeout(typingTimer);
  }, []);

  const startTyping = () => {
    let currentIndex = 0;
    const typingInterval = setInterval(() => {
      if (currentIndex < englishText.length) {
        setDisplayedText(englishText.slice(0, currentIndex + 1));
        currentIndex++;
      } else {
        clearInterval(typingInterval);
        setIsTyping(false);
        setShowCursor(false);

        // Start flowers animation after typing
        setTimeout(() => {
          setShowFlowers(true);

          // Show next button after flowers animation
          setTimeout(() => {
            setShowNextButton(true);
          }, 3000);
        }, 500);
      }
    }, 50); // Typing speed
  };

  const generateFlowers = () => {
    const flowers = [];
    const directions = ['top', 'bottom', 'left', 'right', 'top-left', 'top-right', 'bottom-left', 'bottom-right'];
    const imageTypes = ['cherry-blossom', 'flower', 'sakura'];

    for (let i = 0; i < 24; i++) {
      flowers.push({
        id: `flower-${i}`,
        delay: Math.random() * 2,
        from: directions[i % directions.length],
        imageType: imageTypes[i % imageTypes.length],
        size: Math.random() * 40 + 40
      });
    }

    return flowers;
  };

  return (
    <div className="relative w-full min-h-screen overflow-y-auto bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Main content container */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        className="relative z-20 flex flex-col items-center justify-center min-h-screen px-6"
      >
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
            className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 shadow-2xl"
          >
            <p 
              className="text-lg sm:text-xl md:text-2xl leading-relaxed text-gray-800 font-medium"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                lineHeight: 1.8,
                minHeight: '200px'
              }}
            >
              {displayedText}
              {showCursor && (
                <motion.span
                  animate={{ opacity: [1, 0, 1] }}
                  transition={{ duration: 1, repeat: Infinity }}
                  className="inline-block ml-1 text-pink-500"
                >
                  |
                </motion.span>
              )}
            </p>
          </motion.div>
        </div>

        {/* Next button below text div */}
        <AnimatePresence>
          {showNextButton && (
            <motion.div
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0 }}
              transition={{ duration: 0.5 }}
              className="mt-8 z-30"
            >
              <button
                className="px-8 py-3 bg-white text-pink-500 font-bold rounded-full shadow-lg hover:shadow-xl hover:bg-gray-50 transform hover:scale-105 transition-all duration-300"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                  fontSize: '1.1rem'
                }}
              >
                Next â†’
              </button>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>

      {/* Flowers animation */}
      <AnimatePresence>
        {showFlowers && (
          <div className="absolute inset-0 pointer-events-none">
            {generateFlowers().map((flower) => (
              <Flower
                key={flower.id}
                delay={flower.delay}
                from={flower.from}
                imageType={flower.imageType}
                size={flower.size}
              />
            ))}
          </div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Page2;
