import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Heart from './Heart';
import Page2 from './Page2';
import Page3 from './Page3';
import Page4 from './Page4';
import MiniMusicPlayer from './MiniMusicPlayer';

const Welcome = ({ onNext, audioRef }) => {
  const textVariants = {
    hidden: { opacity: 0, scale: 0.5, y: 50 },
    visible: { 
      opacity: 1, 
      scale: 1,
      y: 0,
      transition: { duration: 1.5, ease: "easeOut" }
    }
  };

  const handleLetGo = () => {
    // Play music
    if (audioRef && audioRef.current) {
      audioRef.current.play().catch(e => console.log("Play failed:", e));
    }
    // Go to next page
    onNext();
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-rose-300 via-pink-300 to-red-200 flex items-center justify-center">
      {/* Animated background hearts */}
      <motion.div
        className="absolute top-10 right-10 text-5xl"
        animate={{ y: [0, -30, 0] }}
        transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’•
      </motion.div>
      <motion.div
        className="absolute bottom-20 left-10 text-5xl"
        animate={{ y: [0, -20, 0], x: [0, 15, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
      >
        ðŸ’–
      </motion.div>
      <motion.div
        className="absolute top-1/3 right-20 text-4xl"
        animate={{ scale: [1, 1.3, 1] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’—
      </motion.div>

      {/* Main content */}
      <div className="text-center px-4 z-10">
        <motion.h1
          variants={textVariants}
          initial="hidden"
          animate="visible"
          className="text-5xl sm:text-6xl md:text-7xl lg:text-8xl font-bold text-white drop-shadow-2xl mb-8"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '4px 4px 8px rgba(0,0,0,0.3), 0 0 30px rgba(255,255,255,0.6)',
            whiteSpace: 'pre-line'
          }}
        >
          My loveeeeee
          <br />
          get readyyyyyy
        </motion.h1>

        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.5, duration: 0.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLetGo}
          className="mt-12 px-12 py-4 bg-white text-red-500 font-bold rounded-full shadow-2xl hover:shadow-2xl transform transition-all duration-300 text-2xl sm:text-3xl"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Let's goooooo
        </motion.button>
      </div>
    </div>
  );
};

const Page1 = ({ onNext }) => {
  const [hearts, setHearts] = useState([]);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      // Floating hearts
      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      // Pulsing hearts
      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      // Small static hearts
      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();
  }, []);

  const textBoxVariants = {
    hidden: { opacity: 0, x: -100 },
    visible: (i) => ({
      opacity: 1,
      x: 0,
      transition: { delay: i * 0.8, duration: 0.8, ease: "easeOut" }
    }),
    exit: { opacity: 0, x: 100, transition: { duration: 0.5 } }
  };

  const texts = [
    "I wanted to tell how much I love you ðŸ’•",
    "You are the best thing that happened to me recently",
    "You are the most beautiful person I know",
    "Every moment with you feels like a dream",
    "Your smile brightens up my darkest days",
    "I love the way you laugh",
    "You make my heart skip a beat",
    "With you, I feel complete",
    "You are my greatest blessing",
    "I cherish every second with you",
    "You inspire me to be better",
    "My world is better with you",
    "You are my forever Valentine",
    "I want to spend forever with you"
  ];

  const mainText = "Wazaaaaap Medooooooooooooooo";

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Animated Hearts Background */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      {/* Header with Main Text */}
      <div className="relative w-full pt-8 sm:pt-12 text-center z-20">
        <motion.h1 
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white drop-shadow-2xl px-4"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '3px 3px 6px rgba(0,0,0,0.3), 0 0 20px rgba(255,255,255,0.5)',
          }}
        >
          {mainText}
        </motion.h1>
      </div>

      {/* Text Boxes Coming from Left with Arrows Between */}
      <div className="absolute inset-0 flex flex-col items-center justify-center px-3 sm:px-4 md:px-6 py-8 overflow-y-auto">
        <div className="w-full max-w-2xl">
          <AnimatePresence>
            {texts.map((text, index) => (
              <div key={index}>
                <motion.div
                  custom={index}
                  variants={textBoxVariants}
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                  className="bg-white bg-opacity-95 px-4 sm:px-6 py-2 sm:py-3 rounded-lg shadow-lg mb-2 sm:mb-3"
                >
                  <p className="text-xs sm:text-sm md:text-base font-medium text-gray-800 text-center"
                     style={{ fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive" }}>
                    {text}
                  </p>
                </motion.div>

                {index < texts.length - 1 && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: (index + 1) * 0.8 - 0.3, duration: 0.5 }}
                    className="text-center text-lg sm:text-2xl text-white drop-shadow-lg py-1"
                  >
                    â†“
                  </motion.div>
                )}
              </div>
            ))}
          </AnimatePresence>
        </div>
      </div>

      {/* Next Button at Bottom */}
      <div className="absolute bottom-8 sm:bottom-12 right-4 sm:right-8 z-30">
        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 4.5, duration: 0.5 }}
          onClick={onNext}
          className="px-6 sm:px-8 py-2 sm:py-3 bg-white text-pink-500 font-bold rounded-full shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 text-base sm:text-lg"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Continue â†’
        </motion.button>
      </div>
    </div>
  );
};

