import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const MusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false); // Don't auto-start on mobile
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0); // Start with first Mac DeMarco song
  const [showDropdown, setShowDropdown] = useState(false);
  const [hasInteracted, setHasInteracted] = useState(false);

  const audioRef = useRef(null);

  // Auto-generated playlist from music folder
  const playlist = [
    { name: "No Other Heart", artist: "Mac DeMarco", src: "/music/Mac DeMarco __ No Other Heart (Official Audio) - CapturedTracks.mp3", cover: "/covers/no-other-heart.jpg" },
    { name: "I Like Her", artist: "Mac DeMarco", src: "/music/20191009 I Like Her - Mac DeMarco.mp3", cover: "/covers/i-like-her.jpg" },
    { name: "For the First Time", artist: "Mac DeMarco", src: "/music/For the First Time - Mac DeMarco.mp3", cover: "/covers/for-the-first-time.jpg" },
    { name: "Heart To Heart", artist: "Mac DeMarco", src: "/music/Heart To Heart - Mac DeMarco.mp3", cover: "/covers/heart-to-heart.jpg" },
    { name: "My Kind Of Woman", artist: "Mac DeMarco", src: "/music/Mac DeMarco __ My Kind Of Woman - CapturedTracks.mp3", cover: "/covers/my-kind-of-woman.jpg" },
    { name: "Here Comes The Sun", artist: "The Beatles", src: "/music/Here Comes The Sun (2019 Mix) - The Beatles.mp3", cover: "/covers/here-comes-the-sun.jpg" },
    { name: "Yesterday", artist: "The Beatles", src: "/music/Yesterday (Remastered 2015) - The Beatles.mp3", cover: "/covers/yesterday.jpg" },
    { name: "No Surprises", artist: "Radiohead", src: "/music/Radiohead - No Surprises - Radiohead.mp3", cover: "/covers/no-surprises.jpg" },
    { name: "Please, Please, Please Let Me Get What I Want", artist: "The Smiths", src: "/music/The Smiths - Please, Please, Please Let Me Get What I Want - The Smiths.mp3", cover: "/covers/please-please-please.jpg" },
    { name: "There Is A Light That Never Goes Out", artist: "The Smiths", src: "/music/The Smiths - There Is A Light That Never Goes Out (Official Audio) - The Smiths.mp3", cover: "/covers/there-is-a-light.jpg" },
  ];

  const currentTrack = playlist[currentTrackIndex];

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // Update time
    const updateTime = () => setCurrentTime(audio.currentTime);
    const updateDuration = () => setDuration(audio.duration);

    audio.addEventListener('timeupdate', updateTime);
    audio.addEventListener('loadedmetadata', updateDuration);

    // Handle track end
    const handleEnded = () => {
      if (currentTrackIndex < playlist.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      } else {
        setIsPlaying(false);
      }
    };

    audio.addEventListener('ended', handleEnded);

    return () => {
      audio.removeEventListener('timeupdate', updateTime);
      audio.removeEventListener('loadedmetadata', updateDuration);
      audio.removeEventListener('ended', handleEnded);
    };
  }, [currentTrackIndex, playlist.length]);

  useEffect(() => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play().catch(e => console.log("Audio play failed:", e));
      } else {
        audioRef.current.pause();
      }
    }
  }, [isPlaying, currentTrackIndex]);

  const togglePlay = () => {
    if (!hasInteracted) {
      setHasInteracted(true);
    }
    setIsPlaying(!isPlaying);
  };

  const selectTrack = (index) => {
    if (!hasInteracted) {
      setHasInteracted(true);
    }
    setCurrentTrackIndex(index);
    setIsPlaying(true);
    setShowDropdown(false);
  };

  const nextTrack = () => {
    if (!hasInteracted) {
      setHasInteracted(true);
    }
    if (currentTrackIndex < playlist.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    }
  };

  const prevTrack = () => {
    if (!hasInteracted) {
      setHasInteracted(true);
    }
    if (currentTrackIndex > 0) {
      setCurrentTrackIndex(currentTrackIndex - 1);
    }
  };

  const handleSeek = (e) => {
    const newTime = parseFloat(e.target.value);
    setCurrentTime(newTime);
    if (audioRef.current) {
      audioRef.current.currentTime = newTime;
    }
  };

  const formatTime = (time) => {
    if (isNaN(time)) return "0:00";
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <>
      <audio
        ref={audioRef}
        src={currentTrack.src}
        onLoadedData={() => {
          if (isPlaying && audioRef.current) {
            audioRef.current.play().catch(e => console.log("Auto-play prevented"));
          }
        }}
      />

      <div className="fixed top-3 right-3 z-50 bg-gradient-to-r from-pink-500/90 to-rose-500/90 backdrop-blur-lg rounded-2xl shadow-2xl p-3 w-72 border border-white/20">
        {/* Current Track Info */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex-1 min-w-0">
            <h3 className="text-sm font-bold text-white truncate">{currentTrack.name}</h3>
            <p className="text-xs text-white/80">{currentTrack.artist}</p>
          </div>

          {/* Dropdown Toggle */}
          <div className="relative">
            <button
              onClick={() => setShowDropdown(!showDropdown)}
              className="ml-2 p-2 bg-white/20 hover:bg-white/30 rounded-lg transition-all duration-200 backdrop-blur-sm"
            >
              <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10 6a2 2 0 110-4 2 2 0 010 4zM10 12a2 2 0 110-4 2 2 0 010 4zM10 18a2 2 0 110-4 2 2 0 010 4z"/>
              </svg>
            </button>

            {/* Playlist Dropdown */}
            <AnimatePresence>
              {showDropdown && (
                <motion.div
                  initial={{ opacity: 0, y: -10, scale: 0.95 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  exit={{ opacity: 0, y: -10, scale: 0.95 }}
                  className="absolute top-full right-0 mt-2 w-72 bg-white/95 backdrop-blur-xl rounded-2xl shadow-2xl border border-white/30 max-h-64 overflow-y-auto"
                >
                  {playlist.map((track, index) => (
                    <button
                      key={index}
                      onClick={() => selectTrack(index)}
                      className={`w-full text-left px-4 py-3 hover:bg-white/40 transition-all duration-200 border-b border-white/10 last:border-b-0 ${
                        index === currentTrackIndex ? 'bg-gradient-to-r from-pink-500/20 to-rose-500/20 text-white font-semibold' : 'text-gray-700'
                      }`}
                    >
                      <div className="flex flex-col">
                        <div className="flex items-center">
                          <span className="mr-3">{index === currentTrackIndex && isPlaying ? 'ðŸŽµ' : 'ðŸ’«'}</span>
                          <span className="font-medium truncate">{track.name}</span>
                        </div>
                        <span className="text-xs text-gray-500 ml-7">{track.artist}</span>
                      </div>
                    </button>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="mb-3">
          <div className="flex items-center justify-between text-xs text-white/80 mb-2">
            <span>{formatTime(currentTime)}</span>
            <span>{formatTime(duration)}</span>
          </div>
          <div className="relative">
            <div className="w-full h-2 bg-white/20 rounded-full overflow-hidden">
              <div 
                className="h-full bg-gradient-to-r from-pink-400 to-rose-400 rounded-full transition-all duration-300"
                style={{ width: `${duration ? (currentTime / duration) * 100 : 0}%` }}
              />
            </div>
            <input
              type="range"
              min="0"
              max={duration || 0}
              value={currentTime}
              onChange={handleSeek}
              className="absolute inset-0 w-full h-2 opacity-0 cursor-pointer slider"
            />
          </div>
        </div>

        {/* Controls */}
        <div className="flex items-center justify-center gap-2">
          <button
            onClick={prevTrack}
            disabled={currentTrackIndex === 0}
            className="p-2 bg-white/20 hover:bg-white/30 rounded-full transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed backdrop-blur-sm"
          >
            <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
              <path d="M8.445 14.832A1 1 0 0010 14v-8a1 1 0 00-1.555-.832L3 9.168v-2.5a1 1 0 00-2 0v7a1 1 0 002 0v-2.5l5.445 4z"/>
            </svg>
          </button>

          <button
            onClick={togglePlay}
            className="p-3 bg-white hover:bg-white/90 text-pink-500 rounded-full transition-all duration-200 shadow-lg hover:shadow-xl transform hover:scale-105"
          >
            {isPlaying ? (
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zM7 8a1 1 0 012 0v4a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v4a1 1 0 102 0V8a1 1 0 00-1-1z"/>
              </svg>
            ) : (
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z"/>
              </svg>
            )}
          </button>

          <button
            onClick={nextTrack}
            disabled={currentTrackIndex === playlist.length - 1}
            className="p-2 bg-white/20 hover:bg-white/30 rounded-full transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed backdrop-blur-sm"
          >
            <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
              <path d="M4.555 5.168A1 1 0 003 6v8a1 1 0 001.555.832L10 10.832V14a1 1 0 002 0V6a1 1 0 00-2 0v3.168L4.555 5.168z"/>
            </svg>
          </button>
        </div>
      </div>

      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          width: 12px;
          height: 12px;
          background: #ec4899;
          border-radius: 50%;
          cursor: pointer;
        }
        .slider::-moz-range-thumb {
          width: 12px;
          height: 12px;
          background: #ec4899;
          border-radius: 50%;
          cursor: pointer;
          border: none;
        }
      `}</style>
    </>
  );
};

export default MusicPlayer;
