import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Lily = ({ delay, from, size = 40 }) => {
  const getInitialPosition = () => {
    switch (from) {
      case 'top':
        return { x: `${Math.random() * 100}%`, y: -100 };
      case 'bottom':
        return { x: `${Math.random() * 100}%`, y: 'calc(100vh + 100px)' };
      case 'left':
        return { x: -100, y: `${Math.random() * 100}%` };
      case 'right':
        return { x: 'calc(100vw + 100px)', y: `${Math.random() * 100}%` };
      case 'top-left':
        return { x: -100, y: -100 };
      case 'top-right':
        return { x: 'calc(100vw + 100px)', y: -100 };
      case 'bottom-left':
        return { x: -100, y: 'calc(100vh + 100px)' };
      case 'bottom-right':
        return { x: 'calc(100vw + 100px)', y: 'calc(100vh + 100px)' };
      default:
        return { x: `${Math.random() * 100}%`, y: -100 };
    }
  };

  const getFinalPosition = () => {
    const positions = [
      { x: '10%', y: '10%' },
      { x: '90%', y: '10%' },
      { x: '10%', y: '90%' },
      { x: '90%', y: '90%' },
      { x: '5%', y: '50%' },
      { x: '95%', y: '50%' },
      { x: '50%', y: '5%' },
      { x: '50%', y: '95%' },
    ];
    return positions[Math.floor(Math.random() * positions.length)];
  };

  return (
    <motion.div
      className="absolute"
      style={{
        fontSize: size,
        zIndex: 10,
      }}
      initial={getInitialPosition()}
      animate={getFinalPosition()}
      transition={{
        delay: delay,
        duration: 2,
        ease: "easeOut",
      }}
    >
      ü§ç
    </motion.div>
  );
};

const Page2 = () => {
  const [displayedText, setDisplayedText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showLilies, setShowLilies] = useState(false);
  const [showNextButton, setShowNextButton] = useState(false);
  const [showCursor, setShowCursor] = useState(true);

  const englishText = "I know I haven't told you properly for so long how much I love you, but I want you to know ‚Äî I'm trying to show you everything through my actions. You are the best person, you understand me best, and that's why I made this little gift for you.";

  useEffect(() => {
    // Start typing animation immediately when page loads
    const typingTimer = setTimeout(() => {
      setIsTyping(true);
      startTyping();
    }, 100);

    return () => clearTimeout(typingTimer);
  }, []);

  const startTyping = () => {
    let currentIndex = 0;
    const typingInterval = setInterval(() => {
      if (currentIndex < englishText.length) {
        setDisplayedText(englishText.slice(0, currentIndex + 1));
        currentIndex++;
      } else {
        clearInterval(typingInterval);
        setIsTyping(false);
        setShowCursor(false);

        // Start lilies animation after typing
        setTimeout(() => {
          setShowLilies(true);

          // Show next button after lilies animation
          setTimeout(() => {
            setShowNextButton(true);
          }, 2500);
        }, 500);
      }
    }, 50); // Typing speed
  };

  const generateLilies = () => {
    const lilies = [];
    const directions = ['top', 'bottom', 'left', 'right', 'top-left', 'top-right', 'bottom-left', 'bottom-right'];

    for (let i = 0; i < 16; i++) {
      lilies.push({
        id: `lily-${i}`,
        delay: Math.random() * 1.5,
        from: directions[i % directions.length],
        size: Math.random() * 20 + 30
      });
    }

    return lilies;
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Subtle background hearts - same animation speed as Page 1 */}
      <div className="absolute inset-0">
        {[...Array(8)].map((_, i) => (
          <div
            key={`bg-heart-${i}`}
            className="absolute text-pink-300"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              fontSize: `${Math.random() * 30 + 20}px`,
              animation: `float ${Math.random() * 3 + 4}s ease-in-out infinite`,
              animationDelay: `${Math.random() * 3}s`
            }}
          >
            üíï
          </div>
        ))}
      </div>

      {/* Main content container */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        className="relative z-20 flex items-center justify-center h-full px-6"
      >
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
            className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 shadow-2xl"
          >
            <p 
              className="text-lg sm:text-xl md:text-2xl leading-relaxed text-gray-800 font-medium"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                lineHeight: 1.8,
                minHeight: '200px'
              }}
            >
              {displayedText}
              {showCursor && (
                <motion.span
                  animate={{ opacity: [1, 0, 1] }}
                  transition={{ duration: 1, repeat: Infinity }}
                  className="inline-block ml-1 text-pink-500"
                >
                  |
                </motion.span>
              )}
            </p>
          </motion.div>
        </div>
      </motion.div>

      {/* Lilies animation */}
      <AnimatePresence>
        {showLilies && (
          <div className="absolute inset-0 pointer-events-none">
            {generateLilies().map((lily) => (
              <Lily
                key={lily.id}
                delay={lily.delay}
                from={lily.from}
                size={lily.size}
              />
            ))}
          </div>
        )}
      </AnimatePresence>

      {/* Next button */}
      <AnimatePresence>
        {showNextButton && (
          <motion.div
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0 }}
            transition={{ duration: 0.5 }}
            className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-30"
          >
            <button
              className="px-8 py-3 bg-white text-pink-500 font-bold rounded-full shadow-lg hover:shadow-xl hover:bg-gray-50 transform hover:scale-105 transition-all duration-300"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                fontSize: '1.1rem'
              }}
            >
              Next ‚Üí
            </button>
          </motion.div>
        )}
      </AnimatePresence>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(5deg); }
        }
      `}</style>
    </div>
  );
};

export default Page2;
