import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Heart from './Heart';
import Page2 from './Page2';
import Page3 from './Page3';
import Page4 from './Page4';
import MiniMusicPlayer from './MiniMusicPlayer';

const Welcome = ({ onNext, audioRef }) => {
  const [hearts, setHearts] = useState([]);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();
  }, []);

  const handleLetGo = () => {
    if (audioRef && audioRef.current) {
      audioRef.current.play().catch(e => console.log("Play failed:", e));
    }
    onNext();
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-rose-300 via-pink-300 to-red-200 flex items-center justify-center">
      {/* Background Hearts */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      <div className="text-center px-4 z-10">
        <h1
          className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-bold text-white drop-shadow-2xl mb-12"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '4px 4px 8px rgba(0,0,0,0.3), 0 0 30px rgba(255,255,255,0.6)',
          }}
        >
          Wazaaaaap Medooooooooooooooo
        </h1>

        <button
          onClick={handleLetGo}
          className="px-10 sm:px-14 py-3 sm:py-4 bg-white text-red-500 font-bold rounded-full shadow-2xl hover:shadow-3xl hover:scale-105 active:scale-95 transition-all duration-300 text-xl sm:text-2xl md:text-3xl"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Let's goooooo
        </button>
      </div>
    </div>
  );
};

// Heart Leaf Component
const HeartLeaf = ({ delay, x, y, rotation, size = 1 }) => {
  return (
    <motion.div
      className="absolute"
      style={{
        left: x,
        top: y,
        transformOrigin: 'center center',
      }}
      initial={{ scale: 0, opacity: 0, rotate: rotation }}
      animate={{ scale: size, opacity: 1, rotate: rotation }}
      transition={{
        duration: 0.8,
        delay,
        ease: [0.34, 1.56, 0.64, 1],
      }}
    >
      <motion.div
        animate={{
          scale: [1, 1.1, 1],
          rotate: [rotation, rotation + 5, rotation - 5, rotation],
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      >
        <div
          style={{
            width: `${20 * size}px`,
            height: `${20 * size}px`,
            position: 'relative',
          }}
        >
          <div
            style={{
              position: 'absolute',
              width: '100%',
              height: '100%',
              background: 'linear-gradient(135deg, #ff6b9d 0%, #c44569 100%)',
              transform: 'rotate(-45deg)',
              borderRadius: '50% 50% 0 50%',
              boxShadow: '0 2px 8px rgba(255, 107, 157, 0.5), inset -2px -2px 4px rgba(0,0,0,0.2)',
            }}
          />
        </div>
      </motion.div>

      <motion.div
        className="absolute top-1/2 left-1/2"
        style={{
          width: '4px',
          height: '4px',
          background: '#fff',
          borderRadius: '50%',
          boxShadow: '0 0 10px #fff',
        }}
        animate={{
          opacity: [0, 1, 0],
          scale: [0, 1.5, 0],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          delay: delay + 1,
        }}
      />
    </motion.div>
  );
};

const Page1 = ({ onNext }) => {
  const [hearts, setHearts] = useState([]);
  const [showMessage, setShowMessage] = useState(false);
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();

    const messageTimer = setTimeout(() => {
      setShowMessage(true);
      setTimeout(() => setShowButton(true), 500);
    }, 6000);

    return () => clearTimeout(messageTimer);
  }, []);

  const mainText = "Wazaaaaap Medooooooooooooooo";

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Animated Hearts Background */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      {/* Header */}
      <div className="relative w-full pt-4 sm:pt-6 md:pt-8 text-center z-20">
        <motion.h1 
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white drop-shadow-2xl px-4"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '3px 3px 6px rgba(0,0,0,0.3), 0 0 20px rgba(255,255,255,0.5)',
          }}
        >
          {mainText}
        </motion.h1>
      </div>

      {/* Love Tree Growing from Bottom */}
      <div className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-2xl h-full pointer-events-none z-10">
        <svg className="absolute bottom-0 left-1/2 -translate-x-1/2" width="400" height="600" viewBox="0 0 400 600">
          {/* Main Trunk */}
          <motion.path
            d="M 200 600 L 200 350"
            stroke="url(#trunkGradient)"
            strokeWidth="20"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 2, ease: "easeOut" }}
          />

          {/* Left Main Branch */}
          <motion.path
            d="M 200 400 Q 150 380 100 350"
            stroke="url(#branchGradient)"
            strokeWidth="12"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1.5, delay: 1.5, ease: "easeOut" }}
          />

          {/* Right Main Branch */}
          <motion.path
            d="M 200 400 Q 250 380 300 350"
            stroke="url(#branchGradient)"
            strokeWidth="12"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1.5, delay: 1.5, ease: "easeOut" }}
          />

          {/* Left Secondary Branch */}
          <motion.path
            d="M 200 450 Q 170 440 120 420"
            stroke="url(#branchGradient)"
            strokeWidth="10"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1.2, delay: 2, ease: "easeOut" }}
          />

          {/* Right Secondary Branch */}
          <motion.path
            d="M 200 450 Q 230 440 280 420"
            stroke="url(#branchGradient)"
            strokeWidth="10"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1.2, delay: 2, ease: "easeOut" }}
          />

          {/* Top Left Branch */}
          <motion.path
            d="M 200 370 Q 160 360 130 340"
            stroke="url(#branchGradient)"
            strokeWidth="8"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1, delay: 2.5, ease: "easeOut" }}
          />

          {/* Top Right Branch */}
          <motion.path
            d="M 200 370 Q 240 360 270 340"
            stroke="url(#branchGradient)"
            strokeWidth="8"
            fill="none"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 1, delay: 2.5, ease: "easeOut" }}
          />

          {/* Gradients */}
          <defs>
            <linearGradient id="trunkGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#4a2c1a" />
              <stop offset="50%" stopColor="#6d4c2a" />
              <stop offset="100%" stopColor="#4a2c1a" />
            </linearGradient>
            <linearGradient id="branchGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#5d3a1a" />
              <stop offset="50%" stopColor="#8b5a2b" />
              <stop offset="100%" stopColor="#5d3a1a" />
            </linearGradient>
          </defs>
        </svg>

        {/* Heart Leaves positioned absolutely */}
        <div className="absolute inset-0">
          {/* Left Main Branch Hearts */}
          <HeartLeaf delay={3.2} x="35%" y="42%" rotation={-20} size={1} />
          <HeartLeaf delay={3.4} x="30%" y="44%" rotation={15} size={0.9} />
          <HeartLeaf delay={3.6} x="25%" y="45%" rotation={-10} size={1.1} />

          {/* Right Main Branch Hearts */}
          <HeartLeaf delay={3.2} x="65%" y="42%" rotation={20} size={1} />
          <HeartLeaf delay={3.4} x="70%" y="44%" rotation={-15} size={0.9} />
          <HeartLeaf delay={3.6} x="75%" y="45%" rotation={10} size={1.1} />

          {/* Left Secondary Branch Hearts */}
          <HeartLeaf delay={3.8} x="42%" y="52%" rotation={-25} size={0.85} />
          <HeartLeaf delay={4.0} x="35%" y="54%" rotation={10} size={0.95} />
          <HeartLeaf delay={4.2} x="30%" y="56%" rotation={-15} size={0.9} />

          {/* Right Secondary Branch Hearts */}
          <HeartLeaf delay={3.8} x="58%" y="52%" rotation={25} size={0.85} />
          <HeartLeaf delay={4.0} x="65%" y="54%" rotation={-10} size={0.95} />
          <HeartLeaf delay={4.2} x="70%" y="56%" rotation={15} size={0.9} />

          {/* Top Left Branch Hearts */}
          <HeartLeaf delay={4.4} x="40%" y="39%" rotation={-30} size={0.8} />
          <HeartLeaf delay={4.6} x="33%" y="40%" rotation={20} size={0.85} />

          {/* Top Right Branch Hearts */}
          <HeartLeaf delay={4.4} x="60%" y="39%" rotation={30} size={0.8} />
          <HeartLeaf delay={4.6} x="67%" y="40%" rotation={-20} size={0.85} />

          {/* Center Top Hearts */}
          <HeartLeaf delay={4.8} x="48%" y="36%" rotation={-5} size={1} />
          <HeartLeaf delay={5.0} x="52%" y="36%" rotation={5} size={1} />

          {/* Additional scattered hearts */}
          <HeartLeaf delay={5.2} x="45%" y="45%" rotation={0} size={0.75} />
          <HeartLeaf delay={5.3} x="55%" y="47%" rotation={0} size={0.8} />
          <HeartLeaf delay={5.4} x="38%" y="48%" rotation={-12} size={0.7} />
          <HeartLeaf delay={5.5} x="62%" y="50%" rotation={12} size={0.75} />
        </div>

        {/* Glowing particles around tree */}
        {[...Array(12)].map((_, i) => (
          <motion.div
            key={`particle-${i}`}
            className="absolute"
            style={{
              width: '6px',
              height: '6px',
              background: i % 2 === 0 ? '#ff6b9d' : '#ffd700',
              borderRadius: '50%',
              left: `${40 + (i % 6) * 5}%`,
              top: `${35 + Math.floor(i / 6) * 10}%`,
              filter: 'blur(1px)',
              boxShadow: `0 0 15px ${i % 2 === 0 ? '#ff6b9d' : '#ffd700'}`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0, 1, 0],
              scale: [0, 1.5, 0],
            }}
            transition={{
              duration: 3,
              repeat: Infinity,
              delay: 3 + (i * 0.3),
              ease: "easeInOut",
            }}
          />
        ))}
      </div>

      {/* Message */}
      <AnimatePresence>
        {showMessage && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="absolute top-[20%] sm:top-[22%] left-1/2 -translate-x-1/2 pointer-events-none z-30 px-4"
          >
            <div className="bg-white/95 backdrop-blur-md rounded-2xl sm:rounded-3xl px-6 py-3 sm:px-10 sm:py-5 shadow-2xl">
              <p 
                className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-rose-500 text-center"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                }}
              >
                Our Love Grows 
              </p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Continue Button */}
      <AnimatePresence>
        {showButton && (
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
            className="fixed bottom-0 left-0 right-0 z-50 pb-6 sm:pb-8 flex justify-center"
            style={{
              background: 'linear-gradient(to top, rgba(244, 114, 182, 0.3), transparent)',
              paddingTop: '40px',
            }}
          >
            <motion.button
              onClick={onNext}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="px-10 sm:px-14 md:px-16 py-3 sm:py-4 bg-gradient-to-r from-pink-500 via-rose-500 to-pink-500 text-white font-bold rounded-full shadow-2xl hover:shadow-pink-500/50 transition-all duration-300 text-lg sm:text-xl md:text-2xl"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
              }}
            >
              Continue 
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [playerAudioRef, setPlayerAudioRef] = useState(null);

  const handleNext = () => {
    setCurrentPage(currentPage + 1);
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <MiniMusicPlayer onAudioRef={setPlayerAudioRef} />
      <AnimatePresence mode="wait">
        {currentPage === 0 && (
          <motion.div
            key="welcome"
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6 }}
            className="absolute inset-0"
          >
            <Welcome onNext={handleNext} audioRef={playerAudioRef} />
          </motion.div>
        )}

        {currentPage === 1 && (
          <motion.div
            key="page1"
            initial={{ y: 0 }}
            exit={{ y: '-100%' }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page1 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 2 && (
          <motion.div
            key="page2"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page2 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 3 && (
          <motion.div
            key="page3"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page3 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 4 && (
          <motion.div
            key="page4"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page4 />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default App;
