import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Heart from './Heart';
import Page2 from './Page2';
import Page3 from './Page3';
import Page4 from './Page4';
import MiniMusicPlayer from './MiniMusicPlayer';

const Welcome = ({ onNext, audioRef }) => {
  const textVariants = {
    hidden: { opacity: 0, scale: 0.5, y: 50 },
    visible: { 
      opacity: 1, 
      scale: 1,
      y: 0,
      transition: { duration: 1.5, ease: "easeOut" }
    }
  };

  const handleLetGo = () => {
    if (audioRef && audioRef.current) {
      audioRef.current.play().catch(e => console.log("Play failed:", e));
    }
    onNext();
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-rose-300 via-pink-300 to-red-200 flex items-center justify-center">
      <motion.div
        className="absolute top-10 right-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -30, 0] }}
        transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’•
      </motion.div>
      <motion.div
        className="absolute bottom-20 left-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -20, 0], x: [0, 15, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
      >
        ðŸ’–
      </motion.div>
      <motion.div
        className="absolute top-1/3 right-20 text-2xl sm:text-4xl"
        animate={{ scale: [1, 1.3, 1] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’—
      </motion.div>

      <div className="text-center px-4 z-10">
        <motion.h1
          variants={textVariants}
          initial="hidden"
          animate="visible"
          className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-bold text-white drop-shadow-2xl mb-8"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '4px 4px 8px rgba(0,0,0,0.3), 0 0 30px rgba(255,255,255,0.6)',
            whiteSpace: 'pre-line'
          }}
        >
          My loveeeeee
          <br />
          get readyyyyyy
        </motion.h1>

        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.5, duration: 0.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLetGo}
          className="mt-8 sm:mt-12 px-8 sm:px-12 py-3 sm:py-4 bg-white text-red-500 font-bold rounded-full shadow-2xl hover:shadow-2xl transform transition-all duration-300 text-xl sm:text-2xl md:text-3xl"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Let's goooooo
        </motion.button>
      </div>
    </div>
  );
};

const Flower3D = ({ delay, x, y, rotation, scale, imageSrc }) => {
  return (
    <motion.div
      className="absolute"
      style={{
        left: x,
        top: y,
      }}
      initial={{
        opacity: 0,
        scale: 0,
        rotate: rotation - 180,
        y: -100,
      }}
      animate={{
        opacity: 1,
        scale: scale,
        rotate: rotation,
        y: 0,
      }}
      transition={{
        delay: delay,
        duration: 1.2,
        ease: [0.34, 1.56, 0.64, 1], // Bouncy
      }}
    >
      <motion.img
        src={imageSrc}
        alt="flower"
        className="w-12 h-12 sm:w-16 sm:h-16 md:w-20 md:h-20 object-contain drop-shadow-2xl"
        animate={{
          rotate: [0, 10, -10, 0],
          y: [-5, 5, -5],
        }}
        transition={{
          duration: 4 + Math.random() * 2,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />
    </motion.div>
  );
};

const Page1 = ({ onNext }) => {
  const [hearts, setHearts] = useState([]);
  const [flowers, setFlowers] = useState([]);
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();

    // Create beautiful bouquet arrangement
    const timer = setTimeout(() => {
      const centerX = window.innerWidth / 2;
      const centerY = window.innerHeight / 2;
      const bouquetFlowers = [];

      const flowerImages = [
        'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/cherry-blossom.png',
        'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/flower.png',
        'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/sakura.png',
      ];

      // Create circular bouquet pattern
      const numFlowers = 25;
      const radius = Math.min(window.innerWidth, window.innerHeight) * 0.2;

      for (let i = 0; i < numFlowers; i++) {
        const angle = (i / numFlowers) * Math.PI * 2;
        const layer = Math.floor(i / 8); // Multiple layers
        const currentRadius = radius * (0.5 + layer * 0.3);
        const randomOffset = (Math.random() - 0.5) * 40;

        const x = centerX + Math.cos(angle) * currentRadius + randomOffset - 40;
        const y = centerY + Math.sin(angle) * currentRadius + randomOffset - 40;

        bouquetFlowers.push({
          id: `flower-${i}`,
          x,
          y,
          delay: i * 0.08,
          rotation: Math.random() * 360,
          scale: 0.8 + Math.random() * 0.6,
          imageSrc: flowerImages[i % flowerImages.length],
        });
      }

      setFlowers(bouquetFlowers);

      // Show message after bouquet forms
      setTimeout(() => setShowMessage(true), numFlowers * 80 + 500);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const mainText = "Wazaaaaap Medooooooooooooooo";

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Animated Hearts Background */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      {/* Header with Main Text */}
      <div className="relative w-full pt-6 sm:pt-8 md:pt-12 text-center z-20">
        <motion.h1 
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white drop-shadow-2xl px-4"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '3px 3px 6px rgba(0,0,0,0.3), 0 0 20px rgba(255,255,255,0.5)',
          }}
        >
          {mainText}
        </motion.h1>
      </div>

      {/* 3D Flower Bouquet */}
      <div className="absolute inset-0 pointer-events-none z-10">
        {flowers.map((flower) => (
          <Flower3D
            key={flower.id}
            delay={flower.delay}
            x={flower.x}
            y={flower.y}
            rotation={flower.rotation}
            scale={flower.scale}
            imageSrc={flower.imageSrc}
          />
        ))}
      </div>

      {/* Message that appears after bouquet */}
      <AnimatePresence>
        {showMessage && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8, y: 50 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="absolute inset-0 flex items-center justify-center pointer-events-none z-30"
          >
            <div className="bg-white/95 backdrop-blur-md rounded-3xl px-8 py-6 sm:px-12 sm:py-8 shadow-2xl mx-4 max-w-md">
              <p className="text-2xl sm:text-3xl md:text-4xl font-bold text-pink-600 text-center"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                  textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
                }}
              >
                Just for you ðŸ’•
              </p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Next Button */}
      <div className="absolute bottom-6 sm:bottom-8 md:bottom-12 right-4 sm:right-6 md:right-8 z-40">
        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 3, duration: 0.5 }}
          onClick={onNext}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="px-6 sm:px-8 py-2 sm:py-3 bg-white text-pink-500 font-bold rounded-full shadow-lg hover:shadow-xl transform transition-all duration-300 text-base sm:text-lg pointer-events-auto"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Continue â†’
        </motion.button>
      </div>
    </div>
  );
};

