import React, { useEffect, useMemo, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Flower = ({
  delay,
  imageSrc,
  size = 60,
  initialPos,
  finalPos,
  spinDuration = 1.2,
  driftDuration = 6,
  driftX = [0, 20, -20, 0],
  driftY = [0, -16, 16, 0],
}) => {
  return (
    <motion.div
      className="absolute"
      style={{
        width: size,
        height: size,
        zIndex: 5, // Lower z-index to be in background
      }}
      initial={{ left: initialPos.left, top: initialPos.top }}
      animate={{ left: finalPos.left, top: finalPos.top }}
      transition={{
        delay: delay,
        duration: 2.5,
        ease: "easeOut",
      }}
    >
      <motion.div
        className="w-full h-full"
        animate={{ x: driftX, y: driftY }}
        transition={{
          duration: driftDuration,
          repeat: Infinity,
          repeatType: 'mirror',
          ease: 'easeInOut',
        }}
      >
        <motion.img
          src={imageSrc}
          alt="Flower"
          className="w-full h-full object-contain"
          animate={{ rotate: [0, 360] }}
          transition={{
            duration: spinDuration,
            repeat: Infinity,
            ease: 'linear',
          }}
        />
      </motion.div>
    </motion.div>
  );
};

const Page2 = () => {
  const [displayedText, setDisplayedText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showFlowers, setShowFlowers] = useState(false);
  const [showNextButton, setShowNextButton] = useState(false);
  const [showCursor, setShowCursor] = useState(true);
  const [flowers, setFlowers] = useState([]);
  const typingIntervalRef = useRef(null);
  const timersRef = useRef([]);

  const englishText = "I'm not very good at saying how much I love you. I know I don't always explain my feelings the right way. But I'm always trying to show it with my actions. I'm willing to do anything for you, and I really do my best. I love you even more because you understand this about me. You understand how I am, and you never make me feel bad about it. That's what makes you so special to me.\n\nThat's why I wanted to do something just for you.\náƒ§áƒ•áƒ”áƒšáƒ áƒ§áƒ•áƒáƒ•áƒ˜áƒšáƒ˜ áƒ¨áƒ”áƒœáƒ˜áƒ ðŸŒ¸ðŸ˜";

  useEffect(() => {
    // Start typing animation immediately when page loads
    const t = window.setTimeout(() => {
      setIsTyping(true);
      startTyping();
    }, 100);
    timersRef.current.push(t);

    return () => {
      if (typingIntervalRef.current) window.clearInterval(typingIntervalRef.current);
      timersRef.current.forEach((id) => window.clearTimeout(id));
      timersRef.current = [];
    };
  }, []);

  const startTyping = () => {
    let currentIndex = 0;
    typingIntervalRef.current = window.setInterval(() => {
      if (currentIndex < englishText.length) {
        setDisplayedText(englishText.slice(0, currentIndex + 1));
        currentIndex++;
      } else {
        if (typingIntervalRef.current) window.clearInterval(typingIntervalRef.current);
        typingIntervalRef.current = null;
        setIsTyping(false);
        setShowCursor(false);

        // Start flowers animation after typing
        const t1 = window.setTimeout(() => {
          setShowFlowers(true);
          const t2 = window.setTimeout(() => {
            setShowNextButton(true);
          }, 2200);
          timersRef.current.push(t2);
        }, 500);
        timersRef.current.push(t1);
      }
    }, 50); // Typing speed
  };

  const flowerSources = useMemo(
    () => [
      '/flowers-images/cherry-blossom.png',
      '/flowers-images/flower.png',
      '/flowers-images/sakura.png',
    ],
    []
  );

  const edgeStarts = useMemo(
    () => ['top', 'bottom', 'left', 'right', 'top-left', 'top-right', 'bottom-left', 'bottom-right'],
    []
  );

  const makeInitialPos = (from) => {
    const randVw = () => `${Math.round(Math.random() * 100)}vw`;
    const randVh = () => `${Math.round(Math.random() * 100)}vh`;
    const off = '160px';
    switch (from) {
      case 'top':
        return { left: randVw(), top: `-${off}` };
      case 'bottom':
        return { left: randVw(), top: `calc(100vh + ${off})` };
      case 'left':
        return { left: `-${off}`, top: randVh() };
      case 'right':
        return { left: `calc(100vw + ${off})`, top: randVh() };
      case 'top-left':
        return { left: `-${off}`, top: `-${off}` };
      case 'top-right':
        return { left: `calc(100vw + ${off})`, top: `-${off}` };
      case 'bottom-left':
        return { left: `-${off}`, top: `calc(100vh + ${off})` };
      case 'bottom-right':
        return { left: `calc(100vw + ${off})`, top: `calc(100vh + ${off})` };
      default:
        return { left: randVw(), top: `-${off}` };
    }
  };

  const makeFinalPos = () => {
    // Anywhere on screen (including over the text), stable in vw/vh.
    const rand = (min, max) => Math.random() * (max - min) + min;
    const x = rand(0, 100);
    const y = rand(0, 100);
    return { left: `${Math.round(x)}vw`, top: `${Math.round(y)}vh` };
  };

  useEffect(() => {
    if (!showFlowers) return;

    // Generate once so flowers do NOT jump/re-randomize on re-render.
    const nextFlowers = Array.from({ length: 70 }).map((_, i) => {
      const from = edgeStarts[i % edgeStarts.length];
      const ampX = Math.round(Math.random() * 50 + 18);
      const ampY = Math.round(Math.random() * 40 + 14);
      return {
        id: `flower-${i}`,
        delay: Math.random() * 1.0,
        size: Math.round(Math.random() * 34 + 32),
        imageSrc: flowerSources[i % flowerSources.length],
        spinDuration: Math.random() * 0.6 + 0.7, // faster spin
        driftDuration: Math.random() * 4 + 3,
        driftX: [0, ampX, -ampX, 0],
        driftY: [0, -ampY, ampY, 0],
        initialPos: makeInitialPos(from),
        finalPos: makeFinalPos(),
      };
    });

    setFlowers(nextFlowers);
  }, [edgeStarts, flowerSources, showFlowers]);

  return (
    <div className="relative w-full min-h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Main content container */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        className="relative z-10 flex flex-col items-center justify-center min-h-screen px-6 pointer-events-none"
      >
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
            className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 shadow-2xl"
          >
            <p 
              className="text-lg sm:text-xl md:text-2xl leading-relaxed text-gray-800 font-medium"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                lineHeight: 1.8,
                minHeight: '200px'
              }}
            >
              <span style={{ whiteSpace: 'pre-line' }}>{displayedText}</span>
              {showCursor && (
                <motion.span
                  animate={{ opacity: [1, 0, 1] }}
                  transition={{ duration: 1, repeat: Infinity }}
                  className="inline-block ml-1 text-pink-500"
                >
                  |
                </motion.span>
              )}
            </p>
          </motion.div>
        </div>

        {/* Next button below text div (reserved space to avoid layout glitch) */}
        <div className="mt-8 z-30" style={{ height: 52 }}>
          <AnimatePresence initial={false}>
            {showNextButton && (
              <motion.button
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.95 }}
                transition={{ duration: 0.35, ease: 'easeOut' }}
                className="pointer-events-auto relative z-40 px-8 py-3 bg-white text-pink-500 font-bold rounded-full shadow-lg hover:shadow-xl hover:bg-gray-50 transform hover:scale-105 transition-all duration-300"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                  fontSize: '1.1rem'
                }}
              >
                Next â†’
              </motion.button>
            )}
          </AnimatePresence>
        </div>
      </motion.div>

      {/* Flowers animation (generated once; no jumping) */}
      <AnimatePresence>
        {showFlowers && (
          <div className="absolute inset-0 pointer-events-none" style={{ zIndex: 30 }}>
            {flowers.map((flower) => (
              <Flower
                key={flower.id}
                delay={flower.delay}
                imageSrc={flower.imageSrc}
                size={flower.size}
                initialPos={flower.initialPos}
                finalPos={flower.finalPos}
                spinDuration={flower.spinDuration}
              />
            ))}
          </div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Page2;
