import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const Page3 = () => {
  const [showPhotos, setShowPhotos] = useState(false);
  const [flowers, setFlowers] = useState([]);
  const [fullscreenImage, setFullscreenImage] = useState(null);

  useEffect(() => {
    const t = window.setTimeout(() => setShowPhotos(true), 1200);
    return () => window.clearTimeout(t);
  }, []);

  useEffect(() => {
    const flowerImages = [
      '/flowers-images/cherry-blossom.png',
      '/flowers-images/flower.png',
      '/flowers-images/sakura.png'
    ];

    const generateFlowers = () => {
      const newFlowers = Array.from({ length: 15 }, (_, i) => ({
        id: i,
        image: flowerImages[Math.floor(Math.random() * flowerImages.length)],
        left: Math.random() * 100,
        delay: Math.random() * 5,
        duration: 8 + Math.random() * 6,
        size: 20 + Math.random() * 30,
        rotation: Math.random() * 360,
        sway: -20 + Math.random() * 40
      }));
      setFlowers(newFlowers);
    };

    generateFlowers();
  }, []);

  useEffect(() => {
    if (fullscreenImage) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [fullscreenImage]);

  const images = [
    '/images/178ad021-9848-4178-8bd6-78b4b4a2ad2d.jpg',
    '/images/1b089b47-1dfc-4fd8-9a2e-c8f51c110e2c.jpg',
    '/images/369bd509-b0ab-4a39-a53d-3484666d5aad.jpg',
    '/images/62def098-7c1a-427a-a88a-27c6e9af7cba.jpg',
    '/images/757bf1b6-d286-4fce-8212-471f033f79a8.jpg',
    '/images/9be7576f-1aa7-4c70-84d1-71667d2005b4.jpg',
    '/images/a34a3f96-a5cb-4659-a868-cc92c2ae5f9a.jpg',
    '/images/cf3df111-7637-4e8e-9561-0276cda7355f.jpg',
    '/images/d071059b-1f9b-441c-adbd-3614b27816f8.jpg',
    '/images/ffed03c9-945e-4ae0-a5d7-2789c05eb33e.jpg',
  ];

  const moments = {
    2: "This still makes me smile.",
    5: "I remember this.",
    8: "This feels like us.",
  };

  return (
    <>
      {/* Fullscreen Image Modal */}
      {fullscreenImage && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 z-50 bg-black/90 flex items-center justify-center p-4"
          onClick={() => setFullscreenImage(null)}
        >
          <button
            onClick={() => setFullscreenImage(null)}
            className="absolute top-4 right-4 w-12 h-12 bg-white/20 hover:bg-white/30 backdrop-blur-sm rounded-full flex items-center justify-center text-white text-2xl font-bold transition-all z-50"
            aria-label="Close fullscreen"
          >
            Ã—
          </button>
          <motion.img
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.3 }}
            src={fullscreenImage}
            alt="Fullscreen view"
            className="max-w-full max-h-full object-contain rounded-lg"
            onClick={(e) => e.stopPropagation()}
          />
        </motion.div>
      )}

      {/* Falling flower petals */}
      <div className="fixed inset-0 pointer-events-none z-10 overflow-hidden">
        {flowers.map((flower) => (
          <motion.div
            key={flower.id}
            initial={{ 
              y: -100, 
              x: `${flower.left}vw`,
              rotate: flower.rotation,
              opacity: 0
            }}
            animate={{ 
              y: '110vh',
              x: [`${flower.left}vw`, `${flower.left + flower.sway}vw`, `${flower.left}vw`],
              rotate: [flower.rotation, flower.rotation + 360, flower.rotation + 720],
              opacity: [0, 0.7, 0.7, 0]
            }}
            transition={{
              duration: flower.duration,
              delay: flower.delay,
              repeat: Infinity,
              ease: "linear",
              x: {
                duration: flower.duration / 2,
                repeat: Infinity,
                ease: "easeInOut"
              }
            }}
            style={{
              position: 'absolute',
              width: `${flower.size}px`,
              height: `${flower.size}px`,
            }}
          >
            <img 
              src={flower.image} 
              alt="" 
              className="w-full h-full object-contain drop-shadow-md"
            />
          </motion.div>
        ))}
      </div>

      <div className="w-full bg-gradient-to-br from-pink-200 via-rose-100 to-pink-200 relative">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 pt-6 sm:pt-10 md:pt-14 pb-12 sm:pb-16 md:pb-20 relative z-20">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: [0.22, 1, 0.36, 1] }}
            className="text-center"
          >
            <div
              className="text-xl sm:text-2xl md:text-3xl lg:text-4xl font-semibold text-gray-800 leading-relaxed px-2"
              style={{ fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive" }}
            >
              Here's how far we've come.
            </div>
          </motion.div>

          <div className="mt-6 sm:mt-10 md:mt-12 space-y-5 sm:space-y-7 md:space-y-9">
            {showPhotos &&
              images.map((src, idx) => (
                <React.Fragment key={src}>
                  <motion.div
                    initial={{ opacity: 0, scale: 0.95, y: 30 }}
                    whileInView={{ opacity: 1, scale: 1, y: 0 }}
                    viewport={{ once: true, amount: 0.2, margin: "0px 0px -100px 0px" }}
                    transition={{ 
                      duration: 0.8, 
                      ease: [0.22, 1, 0.36, 1],
                      delay: 0.1
                    }}
                    whileHover={{ 
                      scale: 1.02,
                      transition: { duration: 0.3 }
                    }}
                    className="w-full cursor-pointer"
                    onClick={() => setFullscreenImage(src)}
                  >
                    <div className="w-full overflow-hidden rounded-2xl sm:rounded-3xl shadow-2xl bg-white/60 backdrop-blur-sm">
                      <img
                        src={src}
                        alt={`Memory ${idx + 1}`}
                        className="w-full h-64 sm:h-80 md:h-96 object-cover block"
                        loading="lazy"
                        decoding="async"
                      />
                    </div>
                  </motion.div>

                  {moments[idx] && (
                    <motion.div
                      initial={{ opacity: 0, y: 15 }}
                      whileInView={{ opacity: 1, y: 0 }}
                      viewport={{ once: true, amount: 0.8 }}
                      transition={{ 
                        duration: 0.7, 
                        ease: [0.22, 1, 0.36, 1],
                        delay: 0.2
                      }}
                      className="text-center px-4 relative"
                    >
                      <motion.div
                        animate={{ 
                          rotate: [0, 5, -5, 0],
                          scale: [1, 1.1, 1]
                        }}
                        transition={{
                          duration: 3,
                          repeat: Infinity,
                          ease: "easeInOut"
                        }}
                        className="absolute -left-4 sm:-left-8 top-1/2 -translate-y-1/2 w-8 h-8 sm:w-10 sm:h-10"
                      >
                        <img 
                          src="/flowers-images/cherry-blossom.png" 
                          alt="" 
                          className="w-full h-full object-contain opacity-70"
                        />
                      </motion.div>
                      <motion.div
                        animate={{ 
                          rotate: [0, -5, 5, 0],
                          scale: [1, 1.1, 1]
                        }}
                        transition={{
                          duration: 3,
                          repeat: Infinity,
                          ease: "easeInOut",
                          delay: 1.5
                        }}
                        className="absolute -right-4 sm:-right-8 top-1/2 -translate-y-1/2 w-8 h-8 sm:w-10 sm:h-10"
                      >
                        <img 
                          src="/flowers-images/sakura.png" 
                          alt="" 
                          className="w-full h-full object-contain opacity-70"
                        />
                      </motion.div>
                      <div
                        className="text-base sm:text-lg md:text-xl text-gray-700 italic"
                        style={{ fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive" }}
                      >
                        {moments[idx]}
                      </div>
                    </motion.div>
                  )}
                </React.Fragment>
              ))}
          </div>

          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.98 }}
            whileInView={{ opacity: 1, y: 0, scale: 1 }}
            viewport={{ once: true, amount: 0.5 }}
            transition={{ 
              duration: 1, 
              ease: [0.22, 1, 0.36, 1],
              delay: 0.15
            }}
            className="text-center mt-10 sm:mt-14 md:mt-16 px-4"
          >
            <div
              className="text-lg sm:text-xl md:text-2xl lg:text-3xl font-semibold text-gray-800 leading-relaxed"
              style={{ fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive" }}
            >
              I'm really happy this is our first Valentine.
            </div>
          </motion.div>

          <div className="h-12 sm:h-16 md:h-20" />
        </div>
      </div>
    </>
  );
};

export default Page3;
