      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/WhatsApp%20Image%202026-01-30%20at%203.02.26%20PM%20(14).jpeg',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/WhatsApp%20Image%202026-01-30%20at%203.02.26%20PM%20(15).jpeg',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/WhatsApp%20Image%202026-01-30%20at%203.02.26%20PM%20(16).jpeg',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/WhatsApp%20Image%202026-01-30%20at%203.02.26%20PM%20(17).jpeg',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/WhatsApp%20Image%202026-01-30%20at%203.02.26%20PM%20(18).jpeg',
    ],
    []
  )

  const paginate = (newDirection) => {
    setDirection(newDirection)
    setCurrentIndex((prevIndex) => {
      let nextIndex = prevIndex + newDirection
      if (nextIndex >= images.length) nextIndex = 0
      if (nextIndex < 0) nextIndex = images.length - 1
      return nextIndex
    })
  }

  const handleDragEnd = (e, { offset, velocity }) => {
    const swipe = Math.abs(offset.x) * velocity.x

    if (swipe < -10000) {
      paginate(1)
    } else if (swipe > 10000) {
      paginate(-1)
    }

    dragX.set(0)
  }

  const variants = {
    enter: (direction) => ({
      x: direction > 0 ? 1000 : -1000,
      opacity: 0,
      scale: 0.5,
    }),
    center: {
      zIndex: 1,
      x: 0,
      opacity: 1,
      scale: 1,
    },
    exit: (direction) => ({
      zIndex: 0,
      x: direction < 0 ? 1000 : -1000,
      opacity: 0,
      scale: 0.5,
    }),
  }

  const swipeConfidenceThreshold = 10000
  const swipePower = (offset, velocity) => {
    return Math.abs(offset) * velocity
  }

  return (
    <div className="relative min-h-screen bg-gradient-to-br from-pink-100 via-purple-100 to-pink-50 overflow-hidden flex items-center justify-center">
      {/* Background flowers - animated */}
      <div className="absolute inset-0 overflow-hidden">
        {flowers.map((f) => (
          <motion.img
            key={f.id}
            src={f.image}
            alt="flower"
            className="absolute pointer-events-none"
            style={{
              width: f.size,
              height: f.size,
              left: `${f.left}%`,
              top: `${f.top}%`,
              opacity: f.opacity,
            }}
            initial={{ rotate: f.rotation, y: 0 }}
            animate={{
              y: [0, -30, 0],
              rotate: [f.rotation, f.rotation + 15, f.rotation],
              opacity: [f.opacity, f.opacity * 1.3, f.opacity],
            }}
            transition={{
              duration: 5 + Math.random() * 4,
              repeat: Infinity,
              delay: f.delay,
              ease: 'easeInOut',
            }}
          />
        ))}
      </div>

      {/* Main content */}
      <div className="relative z-20 w-full max-w-7xl px-3 sm:px-4 md:px-6 py-6 sm:py-8">
        <motion.div
          initial={{ opacity: 0, scale: 0.9 }}
          animate={showPhotos ? { opacity: 1, scale: 1 } : {}}
          transition={{ duration: 0.7, ease: 'easeOut' }}
          className="flex flex-col items-center justify-center"
        >
          {/* Header text - responsive */}
          <motion.div
            className="text-center mb-4 sm:mb-6 md:mb-8 z-10 px-4"
            initial={{ opacity: 0, y: -30 }}
            animate={showPhotos ? { opacity: 1, y: 0 } : {}}
            transition={{ delay: 0.3, duration: 0.7 }}
          >
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-600 via-rose-500 to-pink-600 mb-2 font-caveat drop-shadow-lg">
              ·Éõ·Éò·Éß·Éï·Éê·É†·É° ·Éî·É° ·Éß·Éù·Éï·Éî·Éö·Éò·Éï·Éî·Éî·Éî!!!!
            </h2>
            <p className="text-lg sm:text-xl md:text-2xl lg:text-3xl text-rose-600 font-caveat drop-shadow-md">
              ·Éß·Éï·Éï·Éî·Éö·Éê·Éñ·Éî ·Éõ·Éî·É¢·Éê·Éì ·Éó·Éê·Éê·Éê·Éú!!!!!!!!!!!
            </p>
          </motion.div>

          {/* Carousel container - endless smooth swipe */}
          <div className="relative w-full mb-4 sm:mb-6 md:mb-8 overflow-hidden">
            <div className="relative w-full h-[280px] sm:h-[350px] md:h-[450px] lg:h-[550px] flex items-center justify-center">
              <AnimatePresence initial={false} custom={direction}>
                <motion.div
                  key={currentIndex}
                  custom={direction}
                  variants={variants}
                  initial="enter"
                  animate="center"
                  exit="exit"
                  transition={{
                    x: { type: 'spring', stiffness: 300, damping: 30 },
                    opacity: { duration: 0.2 },
                    scale: { duration: 0.2 },
                  }}
                  drag="x"
                  dragConstraints={{ left: 0, right: 0 }}
                  dragElastic={1}
                  onDragEnd={(e, { offset, velocity }) => {
                    const swipe = swipePower(offset.x, velocity.x)

                    if (swipe < -swipeConfidenceThreshold) {
                      paginate(1)
                    } else if (swipe > swipeConfidenceThreshold) {
                      paginate(-1)
                    }
                  }}
                  className="absolute w-full h-full flex items-center justify-center"
                  style={{ x: dragX }}
                >
                  <motion.img
                    src={images[currentIndex]}
                    alt={`memory-${currentIndex}`}
                    className="w-[240px] h-[240px] sm:w-[300px] sm:h-[300px] md:w-[380px] md:h-[380px] lg:w-[460px] lg:h-[460px] rounded-3xl object-cover shadow-2xl border-4 sm:border-6 border-white cursor-grab active:cursor-grabbing select-none"
                    onClick={() => setFullscreenImage(images[currentIndex])}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    draggable={false}
                  />
                </motion.div>
              </AnimatePresence>

              {/* Navigation arrows - visible on larger screens */}
              <button
                onClick={() => paginate(-1)}
                className="absolute left-2 sm:left-4 md:left-6 top-1/2 -translate-y-1/2 z-40 bg-white/90 hover:bg-white text-rose-500 rounded-full w-10 h-10 sm:w-12 sm:h-12 md:w-14 md:h-14 flex items-center justify-center shadow-xl transition-all hover:scale-110 active:scale-95 text-xl sm:text-2xl font-bold"
              >
                ‚ùÆ
              </button>
              <button
                onClick={() => paginate(1)}
                className="absolute right-2 sm:right-4 md:right-6 top-1/2 -translate-y-1/2 z-40 bg-white/90 hover:bg-white text-rose-500 rounded-full w-10 h-10 sm:w-12 sm:h-12 md:w-14 md:h-14 flex items-center justify-center shadow-xl transition-all hover:scale-110 active:scale-95 text-xl sm:text-2xl font-bold"
              >
                ‚ùØ
              </button>
            </div>
          </div>

          {/* Dots indicator - responsive */}
          <motion.div
            className="flex gap-1.5 sm:gap-2 justify-center mb-4 sm:mb-6 md:mb-8 z-10 flex-wrap max-w-md px-4"
            initial={{ opacity: 0 }}
            animate={showPhotos ? { opacity: 1 } : {}}
            transition={{ delay: 0.5 }}
          >
            {images.map((_, idx) => (
              <motion.button
                key={idx}
                className={`h-2 sm:h-2.5 md:h-3 rounded-full transition-all ${
                  idx === currentIndex
                    ? 'bg-rose-500 w-6 sm:w-7 md:w-8'
                    : 'bg-pink-300 hover:bg-pink-400 w-2 sm:w-2.5 md:w-3'
                }`}
                onClick={() => {
                  const newDirection = idx > currentIndex ? 1 : -1
                  setDirection(newDirection)
                  setCurrentIndex(idx)
                }}
                whileHover={{ scale: 1.2 }}
                whileTap={{ scale: 0.9 }}
              />
            ))}
          </motion.div>

          {/* Swipe hint - mobile only */}
          <motion.p
            className="text-xs sm:text-sm text-gray-600 mb-3 sm:mb-4 z-10 text-center px-4 md:hidden"
            initial={{ opacity: 0 }}
            animate={showPhotos ? { opacity: 1 } : {}}
            transition={{ delay: 0.6 }}
          >
            üëÜ Swipe or tap arrows to see more
          </motion.p>

          {/* Next button - responsive */}
          <motion.button
            onClick={onNext}
            className="px-6 sm:px-8 md:px-10 py-2.5 sm:py-3 md:py-3.5 bg-gradient-to-r from-rose-500 to-pink-500 text-white rounded-full font-semibold shadow-xl hover:shadow-2xl transition-all z-10 text-sm sm:text-base md:text-lg"
            initial={{ opacity: 0, y: 20 }}
            animate={showPhotos ? { opacity: 1, y: 0 } : {}}
            transition={{ delay: 0.7 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Next Knig ‚Üí
          </motion.button>
        </motion.div>
      </div>

      {/* Fullscreen image viewer - responsive */}
      <AnimatePresence>
        {fullscreenImage && (
          <motion.div
            className="fixed inset-0 bg-black/95 flex items-center justify-center z-50 p-2 sm:p-4"
            onClick={() => setFullscreenImage(null)}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative w-full h-full max-w-5xl flex items-center justify-center"
              onClick={(e) => e.stopPropagation()}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              transition={{ type: 'spring', damping: 25 }}
            >
              <img
                src={fullscreenImage}
                alt="fullscreen"
                className="max-w-full max-h-full object-contain rounded-xl"
              />
              <button
                onClick={() => setFullscreenImage(null)}
                className="absolute top-2 right-2 sm:top-4 sm:right-4 bg-white text-black rounded-full w-10 h-10 sm:w-12 sm:h-12 flex items-center justify-center text-xl sm:text-2xl font-bold hover:bg-gray-200 transition-colors shadow-lg"
              >
                ‚úï
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  )
}

export default Page3
