import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const MusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(true); // Start playing automatically
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0); // Start with first Mac DeMarco song
  const [showDropdown, setShowDropdown] = useState(false);

  const audioRef = useRef(null);

  // Auto-generated playlist from music folder
  const playlist = [
    { name: "No Other Heart", artist: "Mac DeMarco", src: "/music/Mac DeMarco __ No Other Heart (Official Audio) - CapturedTracks.mp3" },
    { name: "I Like Her", artist: "Mac DeMarco", src: "/music/20191009 I Like Her - Mac DeMarco.mp3" },
    { name: "For the First Time", artist: "Mac DeMarco", src: "/music/For the First Time - Mac DeMarco.mp3" },
    { name: "Heart To Heart", artist: "Mac DeMarco", src: "/music/Heart To Heart - Mac DeMarco.mp3" },
    { name: "My Kind Of Woman", artist: "Mac DeMarco", src: "/music/Mac DeMarco __ My Kind Of Woman - CapturedTracks.mp3" },
    { name: "Here Comes The Sun", artist: "The Beatles", src: "/music/Here Comes The Sun (2019 Mix) - The Beatles.mp3" },
    { name: "Yesterday", artist: "The Beatles", src: "/music/Yesterday (Remastered 2015) - The Beatles.mp3" },
    { name: "No Surprises", artist: "Radiohead", src: "/music/Radiohead - No Surprises - Radiohead.mp3" },
    { name: "Please, Please, Please Let Me Get What I Want", artist: "The Smiths", src: "/music/The Smiths - Please, Please, Please Let Me Get What I Want - The Smiths.mp3" },
    { name: "There Is A Light That Never Goes Out", artist: "The Smiths", src: "/music/The Smiths - There Is A Light That Never Goes Out (Official Audio) - The Smiths.mp3" },
  ];

  const currentTrack = playlist[currentTrackIndex];

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // Set initial volume
    audio.volume = volume;

    // Update time
    const updateTime = () => setCurrentTime(audio.currentTime);
    const updateDuration = () => setDuration(audio.duration);

    audio.addEventListener('timeupdate', updateTime);
    audio.addEventListener('loadedmetadata', updateDuration);

    // Handle track end
    const handleEnded = () => {
      if (currentTrackIndex < playlist.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      } else {
        setIsPlaying(false);
      }
    };

    audio.addEventListener('ended', handleEnded);

    return () => {
      audio.removeEventListener('timeupdate', updateTime);
      audio.removeEventListener('loadedmetadata', updateDuration);
      audio.removeEventListener('ended', handleEnded);
    };
  }, [currentTrackIndex, playlist.length, volume]);

  useEffect(() => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play().catch(e => console.log("Audio play failed:", e));
      } else {
        audioRef.current.pause();
      }
    }
  }, [isPlaying, currentTrackIndex]);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  const selectTrack = (index) => {
    setCurrentTrackIndex(index);
    setIsPlaying(true);
    setShowDropdown(false);
  };

  const nextTrack = () => {
    if (currentTrackIndex < playlist.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    }
  };

  const prevTrack = () => {
    if (currentTrackIndex > 0) {
      setCurrentTrackIndex(currentTrackIndex - 1);
    }
  };

  const handleSeek = (e) => {
    const newTime = parseFloat(e.target.value);
    setCurrentTime(newTime);
    if (audioRef.current) {
      audioRef.current.currentTime = newTime;
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  const formatTime = (time) => {
    if (isNaN(time)) return "0:00";
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <>
      <audio
        ref={audioRef}
        src={currentTrack.src}
        onLoadedData={() => {
          if (isPlaying && audioRef.current) {
            audioRef.current.play().catch(e => console.log("Auto-play prevented"));
          }
        }}
      />

      <div className="fixed top-2 right-2 z-50 bg-white/95 backdrop-blur-md rounded-xl shadow-xl p-2 w-64 border border-white/30">
        {/* Current Track Info */}
        <div className="flex items-center justify-between mb-2">
          <div className="flex-1 min-w-0">
            <h3 className="text-xs font-bold text-gray-800 truncate">{currentTrack.name}</h3>
            <p className="text-xs text-gray-600">{currentTrack.artist}</p>
          </div>

          {/* Dropdown Toggle */}
          <div className="relative">
            <button
              onClick={() => setShowDropdown(!showDropdown)}
              className="ml-1 p-1 bg-pink-100 hover:bg-pink-200 rounded-lg transition-colors"
            >
              <svg className="w-3 h-3 text-pink-600" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10 6a2 2 0 110-4 2 2 0 010 4zM10 12a2 2 0 110-4 2 2 0 010 4zM10 18a2 2 0 110-4 2 2 0 010 4z"/>
              </svg>
            </button>

            {/* Playlist Dropdown */}
            <AnimatePresence>
              {showDropdown && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  className="absolute top-full right-0 mt-2 w-64 bg-white rounded-xl shadow-xl border border-gray-200 max-h-60 overflow-y-auto"
                >
                  {playlist.map((track, index) => (
                    <button
                      key={index}
                      onClick={() => selectTrack(index)}
                      className={`w-full text-left px-4 py-3 hover:bg-pink-50 transition-colors border-b border-gray-100 last:border-b-0 ${
                        index === currentTrackIndex ? 'bg-pink-100 text-pink-700 font-semibold' : 'text-gray-700'
                      }`}
                    >
                      <div className="flex flex-col">
                        <div className="flex items-center">
                          <span className="mr-3">{index === currentTrackIndex && isPlaying ? '‚ñ∂Ô∏è' : 'üéµ'}</span>
                          <span className="font-medium truncate">{track.name}</span>
                        </div>
                        <span className="text-xs text-gray-500 ml-7">{track.artist}</span>
                      </div>
                    </button>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="mb-2">
          <div className="flex items-center justify-between text-xs text-gray-600 mb-1">
            <span>{formatTime(currentTime)}</span>
            <span>{formatTime(duration)}</span>
          </div>
          <input
            type="range"
            min="0"
            max={duration || 0}
            value={currentTime}
            onChange={handleSeek}
            className="w-full h-1 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
            style={{
              background: `linear-gradient(to right, #ec4899 0%, #ec4899 ${(currentTime / duration) * 100}%, #e5e7eb ${(currentTime / duration) * 100}%, #e5e7eb 100%)`
            }}
          />
        </div>

        {/* Controls */}
        <div className="flex items-center justify-center gap-1 mb-2">
          <button
            onClick={prevTrack}
            disabled={currentTrackIndex === 0}
            className="p-1 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
              <path d="M8.445 14.832A1 1 0 0010 14v-8a1 1 0 00-1.555-.832L3 9.168v-2.5a1 1 0 00-2 0v7a1 1 0 002 0v-2.5l5.445 4z"/>
            </svg>
          </button>

          <button
            onClick={togglePlay}
            className="p-2 bg-pink-500 hover:bg-pink-600 text-white rounded-full transition-colors shadow-lg"
          >
            {isPlaying ? (
              <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zM7 8a1 1 0 012 0v4a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v4a1 1 0 102 0V8a1 1 0 00-1-1z"/>
              </svg>
            ) : (
              <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z"/>
              </svg>
            )}
          </button>

          <button
            onClick={nextTrack}
            disabled={currentTrackIndex === playlist.length - 1}
            className="p-1 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
              <path d="M4.555 5.168A1 1 0 003 6v8a1 1 0 001.555.832L10 10.832V14a1 1 0 002 0V6a1 1 0 00-2 0v3.168L4.555 5.168z"/>
            </svg>
          </button>
        </div>
      </div>

      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          width: 12px;
          height: 12px;
          background: #ec4899;
          border-radius: 50%;
          cursor: pointer;
        }
        .slider::-moz-range-thumb {
          width: 12px;
          height: 12px;
          background: #ec4899;
          border-radius: 50%;
          cursor: pointer;
          border: none;
        }
      `}</style>
    </>
  );
};

export default MusicPlayer;
