import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Heart from './Heart';
import Page2 from './Page2';
import Page3 from './Page3';
import Page4 from './Page4';
import MiniMusicPlayer from './MiniMusicPlayer';

const Welcome = ({ onNext, audioRef }) => {
  const textVariants = {
    hidden: { opacity: 0, scale: 0.5, y: 50 },
    visible: { 
      opacity: 1, 
      scale: 1,
      y: 0,
      transition: { duration: 1.5, ease: "easeOut" }
    }
  };

  const handleLetGo = () => {
    if (audioRef && audioRef.current) {
      audioRef.current.play().catch(e => console.log("Play failed:", e));
    }
    onNext();
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-rose-300 via-pink-300 to-red-200 flex items-center justify-center">
      <motion.div
        className="absolute top-10 right-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -30, 0] }}
        transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’•
      </motion.div>
      <motion.div
        className="absolute bottom-20 left-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -20, 0], x: [0, 15, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
      >
        ðŸ’–
      </motion.div>
      <motion.div
        className="absolute top-1/3 right-20 text-2xl sm:text-4xl"
        animate={{ scale: [1, 1.3, 1] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’—
      </motion.div>

      <div className="text-center px-4 z-10">
        <motion.h1
          variants={textVariants}
          initial="hidden"
          animate="visible"
          className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-bold text-white drop-shadow-2xl mb-8"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '4px 4px 8px rgba(0,0,0,0.3), 0 0 30px rgba(255,255,255,0.6)',
            whiteSpace: 'pre-line'
          }}
        >
          My loveeeeee
          <br />
          get readyyyyyy
        </motion.h1>

        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.5, duration: 0.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLetGo}
          className="mt-8 sm:mt-12 px-8 sm:px-12 py-3 sm:py-4 bg-white text-red-500 font-bold rounded-full shadow-2xl hover:shadow-2xl transform transition-all duration-300 text-xl sm:text-2xl md:text-3xl"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Let's goooooo
        </motion.button>
      </div>
    </div>
  );
};

// Grass blade component
const GrassBlade = ({ delay, left, height, rotation }) => {
  return (
    <motion.div
      className="absolute bottom-0"
      style={{ left }}
      initial={{ scaleY: 0, opacity: 0 }}
      animate={{ scaleY: 1, opacity: 1 }}
      transition={{ duration: 1, delay, ease: "easeOut" }}
    >
      <motion.div
        style={{
          width: '3px',
          height,
          background: 'linear-gradient(to top, #0a4d0a, #159f1a, #3bc93f)',
          borderRadius: '2px 2px 0 0',
          transformOrigin: 'bottom center',
          transform: `rotate(${rotation}deg)`,
          boxShadow: 'inset -1px 0 2px rgba(0,0,0,0.3)',
        }}
        animate={{
          rotate: [rotation, rotation + 3, rotation - 3, rotation],
        }}
        transition={{
          duration: 3 + Math.random() * 2,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />
    </motion.div>
  );
};

// Beautiful 3D flower component
const MagicalFlower = ({ delay, left, size = 1 }) => {
  return (
    <motion.div
      className="absolute"
      style={{
        left,
        bottom: '80px',
        transformOrigin: 'bottom center',
      }}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay }}
    >
      {/* Stem */}
      <motion.div
        className="relative mx-auto"
        style={{
          width: `${6 * size}px`,
          background: 'linear-gradient(to right, #0d6d6d, #14a8a8, #0d6d6d)',
          borderRadius: '3px',
        }}
        initial={{ height: 0 }}
        animate={{ height: `${180 * size}px` }}
        transition={{ duration: 2, delay, ease: "easeOut" }}
      >
        {/* Leaves on stem */}
        {[1, 2, 3].map((i) => (
          <motion.div
            key={`leaf-${i}`}
            className="absolute"
            style={{
              width: `${30 * size}px`,
              height: `${20 * size}px`,
              background: 'linear-gradient(135deg, #14a8a8, #0d8d8d)',
              borderRadius: i % 2 === 0 ? '50% 0 50% 0' : '0 50% 0 50%',
              top: `${25 * i}%`,
              [i % 2 === 0 ? 'left' : 'right']: `${-25 * size}px`,
              transformOrigin: i % 2 === 0 ? 'right center' : 'left center',
              boxShadow: 'inset -2px -2px 4px rgba(0,0,0,0.2)',
            }}
            initial={{ scale: 0, rotate: 0 }}
            animate={{ 
              scale: 1,
              rotate: i % 2 === 0 ? -30 : 30,
            }}
            transition={{ 
              duration: 0.8, 
              delay: delay + 1.5 + (i * 0.2), 
              ease: "backOut" 
            }}
          />
        ))}
      </motion.div>

      {/* Flower bloom at top */}
      <motion.div
        className="absolute left-1/2 -translate-x-1/2"
        style={{ 
          top: `${-80 * size}px`,
        }}
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1.2, delay: delay + 2, ease: "backOut" }}
      >
        {/* 5 Petals arranged in circle */}
        {[0, 1, 2, 3, 4].map((i) => {
          const angle = (i * 72) - 90; // 360/5 = 72 degrees
          const radians = (angle * Math.PI) / 180;
          const distance = 15 * size;
          const x = Math.cos(radians) * distance;
          const y = Math.sin(radians) * distance;

          return (
            <motion.div
              key={`petal-${i}`}
              className="absolute"
              style={{
                width: `${35 * size}px`,
                height: `${35 * size}px`,
                background: 'linear-gradient(135deg, #6bf0ff 0%, #23d4e6 50%, #1aa8b8 100%)',
                borderRadius: '50% 50% 50% 0',
                transform: `translate(-50%, -50%) rotate(${angle + 45}deg)`,
                left: `${x}px`,
                top: `${y}px`,
                boxShadow: 'inset -3px -3px 6px rgba(0,0,0,0.2), 0 2px 8px rgba(107, 240, 255, 0.5)',
              }}
              initial={{ scale: 0, rotate: angle }}
              animate={{ 
                scale: 1,
                rotate: angle + 45,
              }}
              transition={{ 
                duration: 0.6, 
                delay: delay + 2 + (i * 0.1), 
                ease: "backOut" 
              }}
            />
          );
        })}

        {/* Center of flower */}
        <motion.div
          className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2"
          style={{
            width: `${25 * size}px`,
            height: `${25 * size}px`,
            background: 'radial-gradient(circle, #fff5a5 0%, #ffd700 50%, #daa520 100%)',
            borderRadius: '50%',
            boxShadow: 'inset 0 -3px 6px rgba(0,0,0,0.3), 0 0 15px rgba(255, 215, 0, 0.6)',
          }}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5, delay: delay + 2.5, ease: "backOut" }}
        >
          {/* Dots in center */}
          {[...Array(8)].map((_, i) => {
            const dotAngle = (i * 45) * Math.PI / 180;
            const dotDist = 6 * size;
            return (
              <div
                key={`dot-${i}`}
                className="absolute"
                style={{
                  width: `${3 * size}px`,
                  height: `${3 * size}px`,
                  background: '#d4a520',
                  borderRadius: '50%',
                  left: `50%`,
                  top: `50%`,
                  transform: `translate(-50%, -50%) translate(${Math.cos(dotAngle) * dotDist}px, ${Math.sin(dotAngle) * dotDist}px)`,
                }}
              />
            );
          })}
        </motion.div>

        {/* Glowing particles */}
        {[...Array(6)].map((_, i) => (
          <motion.div
            key={`glow-${i}`}
            className="absolute left-1/2 top-1/2"
            style={{
              width: `${4 * size}px`,
              height: `${4 * size}px`,
              background: i % 2 === 0 ? '#fff5a5' : '#6bf0ff',
              borderRadius: '50%',
              filter: 'blur(1px)',
              boxShadow: `0 0 10px ${i % 2 === 0 ? '#fff5a5' : '#6bf0ff'}`,
            }}
            animate={{
              x: [0, (i % 3 - 1) * 20 * size],
              y: [0, -40 * size, -80 * size, -120 * size],
              opacity: [0, 1, 1, 0],
              scale: [0, 1, 1, 0.5],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              delay: delay + 3 + (i * 0.5),
              ease: "easeOut",
            }}
          />
        ))}
      </motion.div>

      {/* Gentle sway animation */}
      <motion.div
        className="absolute inset-0"
        animate={{
          rotate: [-2, 2, -2],
        }}
        transition={{
          duration: 4,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />
    </motion.div>
  );
};

const Page1 = ({ onNext }) => {
  const [hearts, setHearts] = useState([]);
  const [showMessage, setShowMessage] = useState(false);
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();

    const messageTimer = setTimeout(() => {
      setShowMessage(true);
      setTimeout(() => setShowButton(true), 500);
    }, 5000);

    return () => clearTimeout(messageTimer);
  }, []);

  const mainText = "Wazaaaaap Medooooooooooooooo";

  // Generate grass blades
  const grassBlades = [...Array(40)].map((_, i) => ({
    id: `grass-${i}`,
    delay: 0.5 + (i * 0.03),
    left: `${(i / 40) * 100}%`,
    height: `${30 + Math.random() * 40}px`,
    rotation: (Math.random() - 0.5) * 15,
  }));

  return (
    <div className="relative w-full min-h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Animated Hearts Background */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      {/* Header */}
      <div className="relative w-full pt-4 sm:pt-6 md:pt-8 text-center z-20">
        <motion.h1 
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white drop-shadow-2xl px-4"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '3px 3px 6px rgba(0,0,0,0.3), 0 0 20px rgba(255,255,255,0.5)',
          }}
        >
          {mainText}
        </motion.h1>
      </div>

      {/* Garden Scene */}
      <div className="absolute bottom-0 left-0 right-0 h-96 pointer-events-none z-10">
        {/* Grass layer */}
        <div className="absolute bottom-0 left-0 right-0 h-20">
          {grassBlades.map((blade) => (
            <GrassBlade
              key={blade.id}
              delay={blade.delay}
              left={blade.left}
              height={blade.height}
              rotation={blade.rotation}
            />
          ))}
        </div>

        {/* Flowers */}
        <MagicalFlower delay={0.8} left="15%" size={0.9} />
        <MagicalFlower delay={1.2} left="50%" size={1.1} />
        <MagicalFlower delay={1.6} left="85%" size={0.95} />
      </div>

      {/* Message */}
      <AnimatePresence>
        {showMessage && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="absolute top-[30%] sm:top-[35%] left-1/2 -translate-x-1/2 pointer-events-none z-30"
          >
            <div className="bg-white/95 backdrop-blur-md rounded-2xl sm:rounded-3xl px-6 py-3 sm:px-10 sm:py-5 shadow-2xl">
              <p 
                className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-rose-500 text-center whitespace-nowrap"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                }}
              >
                Just for you ðŸ’•
              </p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Continue Button */}
      <AnimatePresence>
        {showButton && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
            className="fixed bottom-6 sm:bottom-8 md:bottom-12 left-0 right-0 flex justify-center z-50 px-4"
          >
            <motion.button
              onClick={onNext}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="px-8 sm:px-12 md:px-16 py-3 sm:py-4 bg-gradient-to-r from-pink-500 via-rose-500 to-pink-500 text-white font-bold rounded-full shadow-2xl hover:shadow-pink-500/50 transition-all duration-300 text-lg sm:text-xl md:text-2xl"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
              }}
            >
              Continue â†’
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [playerAudioRef, setPlayerAudioRef] = useState(null);

  const handleNext = () => {
    setCurrentPage(currentPage + 1);
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <MiniMusicPlayer onAudioRef={setPlayerAudioRef} />
      <AnimatePresence mode="wait">
        {currentPage === 0 && (
          <motion.div
            key="welcome"
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6 }}
            className="absolute inset-0"
          >
            <Welcome onNext={handleNext} audioRef={playerAudioRef} />
          </motion.div>
        )}

        {currentPage === 1 && (
          <motion.div
            key="page1"
            initial={{ y: 0 }}
            exit={{ y: '-100%' }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page1 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 2 && (
          <motion.div
            key="page2"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page2 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 3 && (
          <motion.div
            key="page3"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page3 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 4 && (
          <motion.div
            key="page4"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page4 />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default App;
