import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Heart from './Heart';
import Page2 from './Page2';
import Page3 from './Page3';
import Page4 from './Page4';
import MiniMusicPlayer from './MiniMusicPlayer';

const Welcome = ({ onNext, audioRef }) => {
  const textVariants = {
    hidden: { opacity: 0, scale: 0.5, y: 50 },
    visible: { 
      opacity: 1, 
      scale: 1,
      y: 0,
      transition: { duration: 1.5, ease: "easeOut" }
    }
  };

  const handleLetGo = () => {
    if (audioRef && audioRef.current) {
      audioRef.current.play().catch(e => console.log("Play failed:", e));
    }
    onNext();
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-rose-300 via-pink-300 to-red-200 flex items-center justify-center">
      <motion.div
        className="absolute top-10 right-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -30, 0] }}
        transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’•
      </motion.div>
      <motion.div
        className="absolute bottom-20 left-10 text-3xl sm:text-5xl"
        animate={{ y: [0, -20, 0], x: [0, 15, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
      >
        ðŸ’–
      </motion.div>
      <motion.div
        className="absolute top-1/3 right-20 text-2xl sm:text-4xl"
        animate={{ scale: [1, 1.3, 1] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      >
        ðŸ’—
      </motion.div>

      <div className="text-center px-4 z-10">
        <motion.h1
          variants={textVariants}
          initial="hidden"
          animate="visible"
          className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-bold text-white drop-shadow-2xl mb-8"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '4px 4px 8px rgba(0,0,0,0.3), 0 0 30px rgba(255,255,255,0.6)',
            whiteSpace: 'pre-line'
          }}
        >
          My loveeeeee
          <br />
          get readyyyyyy
        </motion.h1>

        <motion.button
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.5, duration: 0.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLetGo}
          className="mt-8 sm:mt-12 px-8 sm:px-12 py-3 sm:py-4 bg-white text-red-500 font-bold rounded-full shadow-2xl hover:shadow-2xl transform transition-all duration-300 text-xl sm:text-2xl md:text-3xl"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
          }}
        >
          Let's goooooo
        </motion.button>
      </div>
    </div>
  );
};

const AnimatedFlower = ({ position, delay, index }) => {
  const petalColors = [
    ['#a7ffee', '#54b8aa'],
    ['#ff85c1', '#ff4da6'],
    ['#ff9fd3', '#ff66b3'],
  ];

  const colors = petalColors[index % 3];

  return (
    <motion.div
      className="absolute bottom-0"
      style={{
        left: position,
        transformOrigin: 'bottom center',
      }}
      animate={{
        rotate: [index % 2 === 0 ? -2 : 2, index % 2 === 0 ? 2 : -2, index % 2 === 0 ? -2 : 2],
      }}
      transition={{
        duration: 4,
        repeat: Infinity,
        ease: "easeInOut",
      }}
    >
      {/* Stem */}
      <motion.div
        className="relative mx-auto"
        style={{
          width: '6px',
          background: 'linear-gradient(to top, transparent 10%, #14757a, #39c6d6)',
          boxShadow: 'inset 0 0 2px rgba(0,0,0,0.5)',
        }}
        initial={{ height: 0 }}
        animate={{ height: '280px' }}
        transition={{ duration: 2, delay: delay, ease: "easeOut" }}
      >
        {/* Leaves on stem */}
        <motion.div
          className="absolute"
          style={{
            width: '28px',
            height: '36px',
            background: 'linear-gradient(to top, rgba(20, 117, 122, 0.4), #39c6d6)',
            borderTopRightRadius: '36px',
            borderBottomLeftRadius: '36px',
            top: '20%',
            left: '90%',
          }}
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: delay + 1.6, ease: "backOut" }}
        />

        <motion.div
          className="absolute"
          style={{
            width: '28px',
            height: '36px',
            background: 'linear-gradient(to top, rgba(20, 117, 122, 0.4), #39c6d6)',
            borderTopLeftRadius: '36px',
            borderBottomRightRadius: '36px',
            top: '12%',
            right: '90%',
          }}
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: delay + 2, ease: "backOut" }}
        />
      </motion.div>

      {/* Flower head */}
      <motion.div
        className="absolute -top-16 left-1/2 -translate-x-1/2"
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, delay: delay + 2.5, ease: "backOut" }}
      >
        {/* 4 Petals */}
        {[0, 1, 2, 3].map((i) => (
          <motion.div
            key={i}
            className="absolute"
            style={{
              width: '32px',
              height: '44px',
              background: `linear-gradient(to top, ${colors[1]}, ${colors[0]})`,
              borderRadius: '51% 49% 47% 53% / 44% 45% 55% 69%',
              bottom: 0,
              left: '50%',
              transformOrigin: 'bottom center',
              boxShadow: 'inset 0 0 8px rgba(255,255,255,0.5)',
              opacity: 0.9,
            }}
            initial={{ opacity: 0, scale: 0 }}
            animate={{ 
              opacity: 0.9, 
              scale: 1,
            }}
            transition={{ 
              duration: 0.5, 
              delay: delay + 2.5 + (i * 0.15), 
              ease: "backOut" 
            }}
            transform={
              i === 0 ? 'translate(-10%, 1%) rotateY(40deg) rotateX(-50deg)' :
              i === 1 ? 'translate(-50%, -4%) rotateX(40deg)' :
              i === 2 ? 'translate(-90%, 0%) rotateY(45deg) rotateX(50deg)' :
              'translate(-0%, 18%) rotateX(70deg) rotate(-43deg)'
            }
          />
        ))}

        {/* Center */}
        <motion.div
          className="absolute left-1/2 top-0 -translate-x-1/2 -translate-y-1/2"
          style={{
            width: '36px',
            height: '16px',
            borderRadius: '50%',
            background: 'white',
          }}
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.4, delay: delay + 3.3, ease: "backOut" }}
        >
          <div
            className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2"
            style={{
              width: '60%',
              height: '60%',
              borderRadius: '50%',
              background: 'linear-gradient(90deg, rgb(255, 235, 18), rgb(255, 206, 0))',
            }}
          />
        </motion.div>

        {/* Light particles */}
        {[...Array(6)].map((_, i) => (
          <motion.div
            key={`light-${i}`}
            className="absolute"
            style={{
              width: '4px',
              height: '4px',
              background: i % 2 === 0 ? '#ffFB00' : '#23f0ff',
              borderRadius: '50%',
              filter: 'blur(1px)',
              left: `${-24 + (i * 8)}px`,
              bottom: 0,
            }}
            animate={{
              y: [0, -20, -60, -120],
              opacity: [0, 1, 1, 0],
              x: [0, (i % 2 === 0 ? -8 : 8), (i % 2 === 0 ? 8 : -8), 0],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              delay: delay + 3 + (i * 0.5),
              ease: "linear",
            }}
          />
        ))}
      </motion.div>
    </motion.div>
  );
};

const Page1 = ({ onNext }) => {
  const [hearts, setHearts] = useState([]);
  const [showMessage, setShowMessage] = useState(false);
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    const generateHearts = () => {
      const newHearts = [];

      for (let i = 0; i < 15; i++) {
        newHearts.push({
          id: `float-${i}`,
          size: Math.random() * 20 + 15,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 5,
          duration: Math.random() * 3 + 4,
          type: 'float'
        });
      }

      for (let i = 0; i < 8; i++) {
        newHearts.push({
          id: `pulse-${i}`,
          size: Math.random() * 25 + 20,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 3,
          duration: Math.random() * 2 + 2,
          type: 'pulse'
        });
      }

      for (let i = 0; i < 12; i++) {
        newHearts.push({
          id: `static-${i}`,
          size: Math.random() * 15 + 10,
          position: {
            x: `${Math.random() * 100}%`,
            y: `${Math.random() * 100}%`
          },
          delay: Math.random() * 2,
          duration: Math.random() * 2 + 1,
          type: 'static'
        });
      }

      setHearts(newHearts);
    };

    generateHearts();

    // Show message and button after animations
    const messageTimer = setTimeout(() => {
      setShowMessage(true);
      setTimeout(() => setShowButton(true), 500);
    }, 4500);

    return () => clearTimeout(messageTimer);
  }, []);

  const mainText = "Wazaaaaap Medooooooooooooooo";

  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Animated Hearts Background */}
      <AnimatePresence>
        {hearts.map((heart) => (
          <Heart
            key={heart.id}
            size={heart.size}
            position={heart.position}
            delay={heart.delay}
            duration={heart.duration}
            type={heart.type}
          />
        ))}
      </AnimatePresence>

      {/* Header with Main Text */}
      <div className="relative w-full pt-4 sm:pt-6 md:pt-8 text-center z-20">
        <motion.h1 
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white drop-shadow-2xl px-4"
          style={{
            fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
            textShadow: '3px 3px 6px rgba(0,0,0,0.3), 0 0 20px rgba(255,255,255,0.5)',
          }}
        >
          {mainText}
        </motion.h1>
      </div>

      {/* Animated Flowers Growing from Bottom */}
      <div className="absolute inset-0 flex items-end justify-center pointer-events-none z-10">
        <div className="relative w-full max-w-4xl h-full flex items-end justify-center">
          <AnimatedFlower position="20%" delay={0.3} index={0} />
          <AnimatedFlower position="50%" delay={0.6} index={1} />
          <AnimatedFlower position="80%" delay={0.9} index={2} />
        </div>
      </div>

      {/* "Just for you" message */}
      <AnimatePresence>
        {showMessage && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="absolute top-[35%] sm:top-[30%] left-1/2 -translate-x-1/2 pointer-events-none z-30"
          >
            <div className="bg-white/95 backdrop-blur-md rounded-2xl sm:rounded-3xl px-8 py-4 sm:px-12 sm:py-6 shadow-2xl">
              <p 
                className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-rose-500 text-center whitespace-nowrap"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                }}
              >
                Just for you ðŸ’•
              </p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Continue Button */}
      <AnimatePresence>
        {showButton && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
            className="fixed bottom-8 sm:bottom-12 md:bottom-16 left-1/2 -translate-x-1/2 z-50"
          >
            <motion.button
              onClick={onNext}
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.95 }}
              className="px-10 sm:px-14 md:px-16 py-3 sm:py-4 md:py-5 bg-gradient-to-r from-pink-500 via-rose-500 to-pink-500 text-white font-bold rounded-full shadow-2xl hover:shadow-3xl transform transition-all duration-300 text-xl sm:text-2xl md:text-3xl"
              style={{
                fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
              }}
            >
              Continue â†’
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [playerAudioRef, setPlayerAudioRef] = useState(null);

  const handleNext = () => {
    setCurrentPage(currentPage + 1);
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <MiniMusicPlayer onAudioRef={setPlayerAudioRef} />
      <AnimatePresence mode="wait">
        {currentPage === 0 && (
          <motion.div
            key="welcome"
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6 }}
            className="absolute inset-0"
          >
            <Welcome onNext={handleNext} audioRef={playerAudioRef} />
          </motion.div>
        )}

        {currentPage === 1 && (
          <motion.div
            key="page1"
            initial={{ y: 0 }}
            exit={{ y: '-100%' }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page1 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 2 && (
          <motion.div
            key="page2"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page2 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 3 && (
          <motion.div
            key="page3"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page3 onNext={handleNext} />
          </motion.div>
        )}

        {currentPage === 4 && (
          <motion.div
            key="page4"
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
            className="absolute inset-0"
          >
            <Page4 />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default App;
