import React, { useEffect, useMemo, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Flower = ({
  delay,
  imageSrc,
  size = 60,
  startX,
  startY,
  landX,
  landY,
  rotation,
  floatAmplitude,
  floatDuration,
}) => {
  return (
    <motion.div
      className="absolute pointer-events-none"
      style={{
        width: size,
        height: size,
        zIndex: 5,
      }}
      initial={{ 
        x: startX, 
        y: startY,
        opacity: 0,
        scale: 0.3,
        rotate: Math.random() * 360
      }}
      animate={{
        x: landX,
        y: landY,
        opacity: 1,
        scale: 1,
      }}
      transition={{
        delay,
        duration: 1.5,
        ease: [0.34, 1.56, 0.64, 1], // Bouncy ease
      }}
    >
      <motion.img
        src={imageSrc}
        alt="Flower"
        className="w-full h-full object-contain drop-shadow-lg"
        animate={{
          rotate: [rotation, rotation + 360],
          y: [-floatAmplitude, floatAmplitude, -floatAmplitude],
          scale: [1, 1.05, 1],
        }}
        transition={{
          rotate: {
            duration: floatDuration * 2,
            repeat: Infinity,
            ease: 'linear',
          },
          y: {
            duration: floatDuration,
            repeat: Infinity,
            ease: 'easeInOut',
          },
          scale: {
            duration: floatDuration,
            repeat: Infinity,
            ease: 'easeInOut',
          },
        }}
      />
    </motion.div>
  );
};

const Page2 = ({ onNext }) => {
  const [displayedText, setDisplayedText] = useState('');
  const [showFlowers, setShowFlowers] = useState(false);
  const [showNextButton, setShowNextButton] = useState(false);
  const [showCursor, setShowCursor] = useState(true);
  const [flowers, setFlowers] = useState([]);
  const [viewport, setViewport] = useState({ w: 0, h: 0 });
  const typingIntervalRef = useRef(null);
  const timersRef = useRef([]);
  const readingDelayMsRef = useRef(null);

  const englishText = "I'm not very good at saying how much I love you. I know I don't always explain my feelings the right way. But I'm always trying to show it with my actions. I'm willing to do anything for you, and I really do my best. I love you even more because you understand this about me. You understand how I am, and you never make me feel bad about it. That's what makes you so special to me.\n\nThat's why I wanted to do something just for you.\náƒ§áƒ•áƒ”áƒšáƒ áƒ§áƒ•áƒáƒ•áƒ˜áƒšáƒ˜ áƒ¨áƒ”áƒœáƒ˜áƒ ðŸŒ¸ðŸ˜";

  useEffect(() => {
    const set = () => setViewport({ w: window.innerWidth, h: window.innerHeight });
    set();
    window.addEventListener('resize', set);

    const t = window.setTimeout(() => {
      startTyping();
    }, 100);
    timersRef.current.push(t);

    return () => {
      window.removeEventListener('resize', set);
      if (typingIntervalRef.current) window.clearInterval(typingIntervalRef.current);
      timersRef.current.forEach((id) => window.clearTimeout(id));
      timersRef.current = [];
    };
  }, []);

  const startTyping = () => {
    let currentIndex = 0;
    typingIntervalRef.current = window.setInterval(() => {
      if (currentIndex < englishText.length) {
        setDisplayedText(englishText.slice(0, currentIndex + 1));
        currentIndex++;
      } else {
        if (typingIntervalRef.current) window.clearInterval(typingIntervalRef.current);
        typingIntervalRef.current = null;
        setShowCursor(false);

        if (readingDelayMsRef.current == null) {
          readingDelayMsRef.current = Math.round(2000 + Math.random() * 1000);
        }

        const t1 = window.setTimeout(() => {
          setShowFlowers(true);

          const t2 = window.setTimeout(() => {
            setShowNextButton(true);
          }, 2500);
          timersRef.current.push(t2);
        }, readingDelayMsRef.current);
        timersRef.current.push(t1);
      }
    }, 50);
  };

  const flowerSources = useMemo(
    () => [
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/cherry-blossom.png',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/flower.png',
      'https://xapopyizznjubyostnxn.supabase.co/storage/v1/object/public/Medos%20webb/sakura.png',
    ],
    []
  );

  useEffect(() => {
    if (!showFlowers || !viewport.w || !viewport.h) return;

    const centerX = viewport.w / 2;
    const centerY = viewport.h / 2;

    // Create a beautiful bouquet formation
    const createBouquetFlowers = () => {
      const flowers = [];
      const numFlowers = 50;

      // Spiral/circular bouquet pattern
      for (let i = 0; i < numFlowers; i++) {
        const angle = (i / numFlowers) * Math.PI * 4; // Multiple spirals
        const radius = (i / numFlowers) * Math.min(viewport.w, viewport.h) * 0.35;
        const randomOffset = (Math.random() - 0.5) * 80;

        // Position in circular/spiral pattern
        const landX = centerX + Math.cos(angle) * radius + randomOffset - 30;
        const landY = centerY + Math.sin(angle) * radius + randomOffset - 30;

        // Start from random edges for dramatic entrance
        const edge = Math.floor(Math.random() * 4);
        let startX, startY;

        switch(edge) {
          case 0: // top
            startX = Math.random() * viewport.w;
            startY = -100;
            break;
          case 1: // right
            startX = viewport.w + 100;
            startY = Math.random() * viewport.h;
            break;
          case 2: // bottom
            startX = Math.random() * viewport.w;
            startY = viewport.h + 100;
            break;
          case 3: // left
            startX = -100;
            startY = Math.random() * viewport.h;
            break;
          default:
            startX = centerX;
            startY = -100;
        }

        const size = 35 + Math.random() * 40;
        const delay = (i / numFlowers) * 1.5; // Staggered entrance

        flowers.push({
          id: `flower-${i}`,
          delay,
          size,
          imageSrc: flowerSources[i % flowerSources.length],
          startX,
          startY,
          landX: Math.max(0, Math.min(landX, viewport.w - size)),
          landY: Math.max(0, Math.min(landY, viewport.h - size)),
          rotation: Math.random() * 360,
          floatAmplitude: 5 + Math.random() * 10,
          floatDuration: 3 + Math.random() * 3,
        });
      }

      return flowers;
    };

    setFlowers(createBouquetFlowers());
  }, [showFlowers, viewport.w, viewport.h, flowerSources]);

  return (
    <div className="relative w-full min-h-screen overflow-hidden bg-gradient-to-br from-pink-400 via-pink-300 to-rose-200">
      {/* Main content container */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen px-4 sm:px-6 py-8">
        <div className="w-full max-w-4xl mx-auto">
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
            className="bg-white/90 backdrop-blur-md rounded-2xl sm:rounded-3xl p-6 sm:p-8 md:p-10 shadow-2xl"
          >
            <div className="space-y-6">
              {/* Text content */}
              <p 
                className="text-base sm:text-lg md:text-xl lg:text-2xl leading-relaxed text-gray-800 font-medium"
                style={{
                  fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                  lineHeight: 1.8,
                  minHeight: '150px'
                }}
              >
                <span style={{ whiteSpace: 'pre-line' }}>{displayedText}</span>
                {showCursor && (
                  <motion.span
                    animate={{ opacity: [1, 0, 1] }}
                    transition={{ duration: 1, repeat: Infinity }}
                    className="inline-block ml-1 text-pink-500"
                  >
                    |
                  </motion.span>
                )}
              </p>

              {/* Next button - always visible when shown */}
              <AnimatePresence>
                {showNextButton && (
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 20 }}
                    transition={{ duration: 0.5, ease: 'easeOut' }}
                    className="flex justify-center pt-4"
                  >
                    <motion.button
                      onClick={onNext}
                      className="relative z-50 px-8 py-3 sm:px-10 sm:py-4 bg-gradient-to-r from-pink-500 to-rose-500 text-white font-bold rounded-full shadow-xl hover:shadow-2xl hover:from-pink-600 hover:to-rose-600 transform transition-all duration-300"
                      style={{
                        fontFamily: "'Kalam', 'Permanent Marker', 'Caveat', cursive",
                        fontSize: 'clamp(1rem, 2.5vw, 1.25rem)'
                      }}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Next ðŸ’•
                    </motion.button>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Flower bouquet animation */}
      <AnimatePresence>
        {showFlowers && (
          <div className="fixed inset-0 pointer-events-none" style={{ zIndex: 5 }}>
            {flowers.map((flower) => (
              <Flower
                key={flower.id}
                delay={flower.delay}
                imageSrc={flower.imageSrc}
                size={flower.size}
                startX={flower.startX}
                startY={flower.startY}
                landX={flower.landX}
                landY={flower.landY}
                rotation={flower.rotation}
                floatAmplitude={flower.floatAmplitude}
                floatDuration={flower.floatDuration}
              />
            ))}
          </div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Page2;
