import React, { useEffect, useState, useRef, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Enhanced Heart component with glow effects
const Heart = ({ size, position, delay, duration, type = 'float' }) => {
  const heartVariants = {
    float: {
      y: [0, -30, 0],
      x: [0, 10, -10, 0],
      rotate: [0, 5, -5, 0],
    },
    pulse: {
      scale: [1, 1.2, 1],
      opacity: [0.6, 1, 0.6],
    },
    static: {
      scale: [1, 1.1, 1],
    }
  };

  return (
    <motion.div
      className="absolute"
      style={{
        left: position.x,
        top: position.y,
        fontSize: size,
        filter: 'drop-shadow(0 0 10px rgba(255, 182, 193, 0.8))',
      }}
      initial={{ opacity: 0, scale: 0 }}
      animate={{ 
        opacity: 1, 
        scale: 1,
        ...heartVariants[type]
      }}
      transition={{
        duration: duration,
        delay: delay,
        repeat: Infinity,
        repeatType: "reverse",
        ease: "easeInOut"
      }}
    >
      <div className="text-pink-300" style={{ textShadow: '0 0 20px rgba(255, 192, 203, 0.6)' }}>❤️</div>
    </motion.div>
  );
};
